/**
 * Enhanced Mobile Container Component
 *
 * Provides better mobile UX patterns including:
 * - Improved touch targets
 * - Better responsive layout
 * - Enhanced animations
 * - Gesture support
 * - Safe area handling
 */

import React, { useEffect, useRef, useState } from 'react';
import { useDash0Telemetry } from '../dash0/hooks/useDash0Telemetry';
import '../styles/mobileEnhancements.css';
import { useTheme } from '../utils/useTheme';

interface EnhancedMobileContainerProps {
  children: React.ReactNode;
  className?: string;
  style?: React.CSSProperties;
  enablePullToRefresh?: boolean;
  onRefresh?: () => Promise<void>;
  enableSwipeGestures?: boolean;
  onSwipeLeft?: () => void;
  onSwipeRight?: () => void;
}

const EnhancedMobileContainer: React.FC<EnhancedMobileContainerProps> = ({
  children,
  className = '',
  style = {},
  enablePullToRefresh = false,
  onRefresh,
  enableSwipeGestures = false,
  onSwipeLeft,
  onSwipeRight,
}) => {
  const { theme } = useTheme();
  const telemetry = useDash0Telemetry();
  const containerRef = useRef<HTMLDivElement>(null);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [pullProgress, setPullProgress] = useState(0);

  // Touch state for gestures
  const touchStartRef = useRef<{ x: number; y: number; time: number } | null>(
    null,
  );
  const lastTouchRef = useRef<{ x: number; y: number } | null>(null);

  // Pull to refresh logic
  useEffect(() => {
    if (!enablePullToRefresh || !containerRef.current) return;

    const container = containerRef.current;
    let startY = 0;
    let currentY = 0;
    let isPulling = false;

    const handleTouchStart = (e: TouchEvent) => {
      if (container.scrollTop === 0) {
        startY = e.touches[0].clientY;
        isPulling = true;

        // Track pull-to-refresh initiation
        telemetry.trackUserInteraction({
          action: 'pull_to_refresh_started',
          component: 'EnhancedMobileContainer',
          metadata: {
            scrollPosition: container.scrollTop,
            startY: Math.round(startY),
            hasRefreshHandler: !!onRefresh,
          },
        });
      }
    };

    const handleTouchMove = (e: TouchEvent) => {
      if (!isPulling) return;

      currentY = e.touches[0].clientY;
      const pullDistance = Math.max(0, currentY - startY);
      const maxPull = 120;
      const progress = Math.min(pullDistance / maxPull, 1);

      setPullProgress(progress);

      if (pullDistance > 0) {
        e.preventDefault();
        container.style.transform = `translateY(${Math.min(
          pullDistance * 0.5,
          60,
        )}px)`;
      }
    };

    const handleTouchEnd = async () => {
      if (!isPulling) return;

      const pullTriggered = pullProgress >= 0.6;

      // Track pull-to-refresh completion
      telemetry.trackUserInteraction({
        action: 'pull_to_refresh_completed',
        component: 'EnhancedMobileContainer',
        metadata: {
          pullProgress: Math.round(pullProgress * 100),
          triggered: pullTriggered,
          hasRefreshHandler: !!onRefresh,
          wasRefreshing: isRefreshing,
        },
      });

      if (pullTriggered) {
        telemetry.trackMetric({
          name: 'pull_to_refresh_trigger',
          value: 1,
          tags: {
            progress: String(Math.round(pullProgress * 100)),
            has_handler: String(!!onRefresh),
          },
        });
      }

      isPulling = false;
      container.style.transform = '';

      if (pullTriggered && onRefresh && !isRefreshing) {
        setIsRefreshing(true);
        const refreshStartTime = performance.now();

        try {
          await onRefresh();

          // Track successful refresh
          const refreshDuration = performance.now() - refreshStartTime;
          telemetry.trackMetric({
            name: 'pull_to_refresh_success',
            value: 1,
            tags: {
              duration_ms: String(Math.round(refreshDuration)),
              method: 'pull_gesture',
            },
          });
        } catch (error) {
          // Track refresh error
          telemetry.trackError(error as Error, {
            context: 'pull_to_refresh_error',
            metadata: { method: 'pull_gesture' },
          });
        } finally {
          setIsRefreshing(false);
        }
      }

      setPullProgress(0);
    };

    container.addEventListener('touchstart', handleTouchStart, {
      passive: false,
    });
    container.addEventListener('touchmove', handleTouchMove, {
      passive: false,
    });
    container.addEventListener('touchend', handleTouchEnd);

    return () => {
      container.removeEventListener('touchstart', handleTouchStart);
      container.removeEventListener('touchmove', handleTouchMove);
      container.removeEventListener('touchend', handleTouchEnd);
    };
  }, [enablePullToRefresh, onRefresh, pullProgress, isRefreshing]);

  // Swipe gesture logic
  useEffect(() => {
    if (!enableSwipeGestures || !containerRef.current) return;

    const container = containerRef.current;

    const handleTouchStart = (e: TouchEvent) => {
      const touch = e.touches[0];
      touchStartRef.current = {
        x: touch.clientX,
        y: touch.clientY,
        time: Date.now(),
      };
    };

    const handleTouchMove = (e: TouchEvent) => {
      if (!touchStartRef.current) return;

      const touch = e.touches[0];
      lastTouchRef.current = {
        x: touch.clientX,
        y: touch.clientY,
      };
    };

    const handleTouchEnd = () => {
      if (!touchStartRef.current || !lastTouchRef.current) return;

      const deltaX = lastTouchRef.current.x - touchStartRef.current.x;
      const deltaY = Math.abs(lastTouchRef.current.y - touchStartRef.current.y);
      const deltaTime = Date.now() - touchStartRef.current.time;

      // Swipe detection thresholds
      const minSwipeDistance = 50;
      const maxSwipeTime = 300;
      const maxVerticalMovement = 100;

      const isValidSwipe =
        Math.abs(deltaX) > minSwipeDistance &&
        deltaY < maxVerticalMovement &&
        deltaTime < maxSwipeTime;

      // Track swipe attempt
      telemetry.trackUserInteraction({
        action: 'swipe_gesture_attempt',
        component: 'EnhancedMobileContainer',
        metadata: {
          deltaX: Math.round(deltaX),
          deltaY: Math.round(deltaY),
          deltaTime,
          isValidSwipe,
          direction: deltaX > 0 ? 'right' : 'left',
          hasHandlers: { left: !!onSwipeLeft, right: !!onSwipeRight },
        },
      });

      if (isValidSwipe) {
        const direction = deltaX > 0 ? 'right' : 'left';
        const hasHandler =
          direction === 'right' ? !!onSwipeRight : !!onSwipeLeft;

        // Track successful swipe
        telemetry.trackUserInteraction({
          action: 'swipe_gesture_success',
          component: 'EnhancedMobileContainer',
          metadata: {
            direction,
            distance: Math.round(Math.abs(deltaX)),
            speed: Math.round((Math.abs(deltaX) / deltaTime) * 1000), // pixels per second
            hasHandler,
          },
        });

        telemetry.trackMetric({
          name: 'swipe_gesture',
          value: 1,
          tags: {
            direction,
            distance_bucket: Math.abs(deltaX) > 100 ? 'long' : 'short',
            speed_bucket:
              (Math.abs(deltaX) / deltaTime) * 1000 > 500 ? 'fast' : 'slow',
          },
        });

        if (deltaX > 0 && onSwipeRight) {
          onSwipeRight();
        } else if (deltaX < 0 && onSwipeLeft) {
          onSwipeLeft();
        }
      } else {
        // Track failed swipe attempt
        let failureReason = 'unknown';
        if (Math.abs(deltaX) <= minSwipeDistance)
          failureReason = 'insufficient_distance';
        else if (deltaY >= maxVerticalMovement) failureReason = 'too_vertical';
        else if (deltaTime >= maxSwipeTime) failureReason = 'too_slow';

        telemetry.trackUserInteraction({
          action: 'swipe_gesture_failed',
          component: 'EnhancedMobileContainer',
          metadata: {
            reason: failureReason,
            deltaX: Math.round(deltaX),
            deltaY: Math.round(deltaY),
            deltaTime,
          },
        });
      }

      touchStartRef.current = null;
      lastTouchRef.current = null;
    };

    container.addEventListener('touchstart', handleTouchStart, {
      passive: true,
    });
    container.addEventListener('touchmove', handleTouchMove, { passive: true });
    container.addEventListener('touchend', handleTouchEnd, { passive: true });

    return () => {
      container.removeEventListener('touchstart', handleTouchStart);
      container.removeEventListener('touchmove', handleTouchMove);
      container.removeEventListener('touchend', handleTouchEnd);
    };
  }, [enableSwipeGestures, onSwipeLeft, onSwipeRight]);

  const containerStyle: React.CSSProperties = {
    background: theme.appBackground,
    color: theme.primaryText,
    transition: 'transform 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
    position: 'relative',
    ...style,
  };

  return (
    <div
      ref={containerRef}
      className={`mobile-app-container ${className}`}
      style={containerStyle}
    >
      {/* Pull to refresh indicator */}
      {enablePullToRefresh && pullProgress > 0 && (
        <div
          style={{
            position: 'absolute',
            top: '16px',
            left: '50%',
            transform: 'translateX(-50%)',
            zIndex: 1000,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            opacity: pullProgress,
            transition: 'opacity 0.1s ease',
          }}
        >
          <div
            style={{
              width: '24px',
              height: '24px',
              borderRadius: '50%',
              border: `2px solid ${theme.secondaryText}`,
              borderTop: `2px solid ${theme.primaryText}`,
              animation: isRefreshing ? 'spin 1s linear infinite' : 'none',
              transform: `rotate(${pullProgress * 360}deg)`,
              marginBottom: '8px',
            }}
          />
          <span
            style={{
              fontSize: '12px',
              color: theme.secondaryText,
              fontWeight: '500',
            }}
          >
            {(() => {
              if (isRefreshing) return 'Refreshing...';
              if (pullProgress >= 0.6) return 'Release to refresh';
              return 'Pull to refresh';
            })()}
          </span>
        </div>
      )}

      <div className="mobile-content-area">{children}</div>
    </div>
  );
};

export default EnhancedMobileContainer;

import { useCallback, useEffect, useRef, useState } from 'react';
import { useHaptic } from '../utils/hapticHooks';
import '../utils/locationDiagnostic'; // Load diagnostic tools in development
import { locationService } from '../utils/locationService';
import { logError } from '../utils/logger';
import { offlineStorage } from '../utils/offlineWeatherStorage';
import { searchPerformanceMonitor } from '../utils/searchPerformanceMonitor';
import type { ThemeColors } from '../utils/themeConfig';
import './EnhancedSearchScreen.css';

interface SearchScreenProps {
  theme: ThemeColors;
  onBack: () => void;
  onLocationSelect: (
    cityName: string,
    latitude: number,
    longitude: number
  ) => void;
}

interface SearchResult {
  name: string;
  display_name: string;
  lat: string;
  lon: string;
  country: string;
  state?: string;
}

interface NominatimResult {
  display_name: string;
  lat: string;
  lon: string;
  place_id: number;
  type: string;
  class: string;
  name?: string;
  importance?: number;
  address?: {
    city?: string;
    town?: string;
    village?: string;
    municipality?: string;
    state?: string;
    country?: string;
  };
  relevanceScore?: number;
  cleanDisplayName?: string;
}

/**
 * Create a clean, readable display name from Nominatim result
 */
const createCleanDisplayName = (item: NominatimResult): string => {
  if (item.name) {
    // Use the name if available, with country
    const country = item.address?.country || '';
    const state = item.address?.state || '';

    if (state && country) {
      return `${item.name}, ${state}, ${country}`;
    } else if (country) {
      return `${item.name}, ${country}`;
    }
    return item.name;
  }

  // Fallback to parsing display_name
  const parts = item.display_name.split(',').map(part => part.trim());
  if (parts.length >= 2) {
    // Return first part (city) + last part (country)
    return `${parts[0]}, ${parts[parts.length - 1]}`;
  }

  return item.display_name;
};

/**
 * Enhanced SearchScreen with improved error handling and fallback mechanisms
 */
function EnhancedSearchScreen({
  theme,
  onBack,
  onLocationSelect,
}: Readonly<SearchScreenProps>) {
  const haptic = useHaptic();
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState<NominatimResult[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [recentSearches, setRecentSearches] = useState<SearchResult[]>([]);
  const [locationPermission, setLocationPermission] = useState<
    PermissionState | 'not-supported' | 'unknown'
  >('unknown');
  const [isGettingLocation, setIsGettingLocation] = useState(false);
  const searchTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Load recent searches
  useEffect(() => {
    try {
      const saved = localStorage.getItem('weather-recent-searches');
      if (saved) {
        setRecentSearches(JSON.parse(saved).slice(0, 5));
      }
    } catch (error) {
      logError('Error loading recent searches:', error);
    }
  }, []);

  // Check location permissions on mount
  useEffect(() => {
    const checkPermissions = async () => {
      try {
        const permission = await locationService.checkPermissions();
        setLocationPermission(permission);
      } catch (error) {
        console.warn('Failed to check location permissions:', error);
        setLocationPermission('unknown');
      }
    };

    checkPermissions();
  }, []);

  // Enhanced search function with better error handling
  const performSearch = useCallback(async (query: string) => {
    if (!query.trim() || query.length < 2) {
      setSearchResults([]);
      setError(null);
      return;
    }

    // Start performance tracking
    const trackingId = searchPerformanceMonitor.startSearch('nominatim');

    setIsSearching(true);
    setError(null);

    // NEW: Check offline storage first
    if (!navigator.onLine) {
      try {
        const recentCities = offlineStorage.getRecentCities();
        const queryLower = query.toLowerCase();

        const offlineMatches = recentCities
          .filter(city => city.name.toLowerCase().includes(queryLower))
          .map(
            (city, index) =>
              ({
                display_name: city.name,
                lat: city.lat.toString(),
                lon: city.lon.toString(),
                place_id: index + 1000000, // Use unique IDs for offline results
                type: 'city',
                class: 'place',
                name: city.name,
                address: { city: city.name },
              } as NominatimResult),
          );

        setSearchResults(offlineMatches);

        if (offlineMatches.length === 0) {
          setError(
            'No cached cities match your search. Connect to internet for full search.',
          );
        } else {
          setError(null);
        }

        // End performance tracking - offline
        searchPerformanceMonitor.endSearch(
          trackingId,
          'offline',
          offlineMatches.length,
        );
        setIsSearching(false);
        return;
      } catch (error) {
        logError('Offline search error:', error);
      }
    }

    try {
      // Primary API: OpenStreetMap Nominatim
      const nominatimUrl = `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(
        query,
      )}&format=json&limit=25&addressdetails=1&extratags=1`;

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 8000); // 8 second timeout

      const response = await fetch(nominatimUrl, {
        headers: {
          'User-Agent': 'PremiumWeatherApp/1.0 (weather.andernet.dev)',
          Accept: 'application/json',
        },
        signal: controller.signal,
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(
          `Search API returned ${response.status}: ${response.statusText}`,
        );
      }

      const data: NominatimResult[] = await response.json();

      // Filter and prioritize results with improved logic
      const filteredResults = data
        .filter((item: NominatimResult) => {
          // Include a broader range of location types
          const isValidLocationType =
            item.type === 'city' ||
            item.type === 'town' ||
            item.type === 'village' ||
            item.type === 'municipality' ||
            item.type === 'administrative' ||
            item.type === 'suburb' ||
            item.type === 'neighbourhood' ||
            item.class === 'place' ||
            item.class === 'boundary' ||
            (item.class === 'amenity' && item.type === 'university') ||
            (item.address &&
              (item.address.city || item.address.town || item.address.village));

          // Exclude very specific locations like individual buildings
          const isNotTooSpecific =
            item.type !== 'house' &&
            item.type !== 'building' &&
            item.type !== 'shop' &&
            item.type !== 'restaurant';

          return isValidLocationType && isNotTooSpecific;
        })
        .map(item => {
          // Calculate relevance score for better sorting
          const queryLower = query.toLowerCase();
          const displayName = item.display_name.toLowerCase();
          const name = (item.name || '').toLowerCase();

          let relevanceScore = 0;

          // Exact name match gets highest score
          if (name === queryLower) {
            relevanceScore += 1000;
          } else if (name.startsWith(queryLower)) {
            relevanceScore += 500;
          } else if (name.includes(queryLower)) {
            relevanceScore += 100;
          }

          // Display name partial matches
          if (displayName.includes(queryLower)) {
            relevanceScore += 50;
          }

          // Boost major cities and administrative areas
          if (item.type === 'city' || item.type === 'administrative') {
            relevanceScore += 200;
          }

          // Boost based on importance (if available)
          if (item.importance) {
            relevanceScore += Math.round(item.importance * 100);
          }

          return {
            ...item,
            relevanceScore,
            // Create clean, readable display name
            cleanDisplayName: createCleanDisplayName(item),
          };
        })
        .sort((a, b) => b.relevanceScore - a.relevanceScore) // Sort by relevance
        .slice(0, 10); // Limit to top 10 results

      setSearchResults(filteredResults);

      if (filteredResults.length === 0) {
        setError('No cities found. Try a different search term.');
      }

      // End performance tracking - success
      searchPerformanceMonitor.endSearch(
        trackingId,
        'nominatim',
        filteredResults.length,
      );
    } catch (error: any) {
      logError('Search error:', error);

      if (error.name === 'AbortError') {
        setError(
          'Search timed out. Please check your internet connection and try again.',
        );
      } else if (
        error.message?.includes('Failed to fetch') ||
        error.message?.includes('NetworkError')
      ) {
        setError(
          'Network error. Please check your internet connection and try again.',
        );
      } else {
        setError('Search failed. Please try again in a moment.');
      }

      setSearchResults([]);
      // End performance tracking - error
      searchPerformanceMonitor.endSearch(trackingId, 'nominatim', 0);
    } finally {
      setIsSearching(false);
    }
  }, []);

  // Debounced search
  useEffect(() => {
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    searchTimeoutRef.current = setTimeout(() => {
      performSearch(searchQuery);
    }, 300);

    return () => {
      if (searchTimeoutRef.current) {
        clearTimeout(searchTimeoutRef.current);
      }
    };
  }, [searchQuery, performSearch]);

  // Handle city selection
  const handleCitySelection = useCallback(
    async (city: NominatimResult) => {
      haptic.dataLoad();

      const cityName = city.display_name.split(',')[0].trim();
      const latitude = parseFloat(city.lat);
      const longitude = parseFloat(city.lon);

      if (isNaN(latitude) || isNaN(longitude)) {
        haptic.error();
        setError('Invalid location coordinates. Please try another city.');
        return;
      }

      // Add to recent searches
      const searchResult: SearchResult = {
        name: cityName,
        display_name: city.display_name,
        lat: city.lat,
        lon: city.lon,
        country:
          city.address?.country ||
          city.display_name.split(',').pop()?.trim() ||
          'Unknown',
        state: city.address?.state || city.display_name.split(',')[1]?.trim(),
      };

      const newRecent = [
        searchResult,
        ...recentSearches.filter(
          r => r.lat !== searchResult.lat || r.lon !== searchResult.lon,
        ),
      ].slice(0, 5);

      setRecentSearches(newRecent);

      try {
        localStorage.setItem(
          'weather-recent-searches',
          JSON.stringify(newRecent),
        );
      } catch (error) {
        logError('Error saving recent searches:', error);
      }

      // NEW: Cache location for offline access
      try {
        await offlineStorage.cacheRecentCity(cityName, latitude, longitude);
      } catch (error) {
        logError('Error caching recent city:', error);
      }

      onLocationSelect(cityName, latitude, longitude);
    },
    [haptic, onLocationSelect, recentSearches],
  );

  // Handle recent search selection
  const handleRecentSelect = useCallback(
    async (result: SearchResult) => {
      haptic.selection();
      const latitude = parseFloat(result.lat);
      const longitude = parseFloat(result.lon);

      // Cache location for offline access
      try {
        await offlineStorage.cacheRecentCity(result.name, latitude, longitude);
      } catch (error) {
        logError('Error caching recent city from selection:', error);
      }

      onLocationSelect(result.name, latitude, longitude);
    },
    [haptic, onLocationSelect],
  );

  // Get current location with enhanced error handling
  const getCurrentLocation = useCallback(async () => {
    haptic.dataLoad();
    setError(null);
    setIsGettingLocation(true);

    try {
      // Check if we're in a secure context
      if (!locationService.isSecureContext()) {
        haptic.error();
        setError(
          'Location services require a secure connection (HTTPS). This feature may not work on non-secure websites.',
        );
        return;
      }

      // Check if location is supported
      if (!locationService.isSupported()) {
        haptic.error();
        setError(
          'Geolocation is not supported by this browser. Please search for your city manually.',
        );
        return;
      }

      // Check permissions first
      const permissionState = await locationService.checkPermissions();
      setLocationPermission(permissionState);

      if (permissionState === 'denied') {
        haptic.error();
        setError(
          'Location access is blocked. Please enable location permissions in your browser settings and refresh the page.',
        );
        return;
      }

      // Get location
      const locationResult = await locationService.getCurrentLocation();

      haptic.dataLoad();

      // Use the city name if available, otherwise use "Current Location"
      const cityName = locationResult.cityName || 'Current Location';

      onLocationSelect(
        cityName,
        locationResult.latitude,
        locationResult.longitude,
      );
    } catch (locationError: any) {
      haptic.error();
      logError('Enhanced geolocation error:', locationError);

      // Use the user-friendly message from the location service
      if (locationError.userMessage) {
        setError(locationError.userMessage);
      } else {
        setError(
          'Failed to get your location. Please try again or search for your city manually.',
        );
      }

      // Log additional troubleshooting tips
      const tips = locationService.getLocationTips();
      console.log('Location troubleshooting tips:', tips);
    } finally {
      setIsGettingLocation(false);
    }
  }, [haptic, onLocationSelect]);

  return (
    <div className="enhanced-search-screen">
      {/* Dynamic theme styles */}
      <style
        dangerouslySetInnerHTML={{
          __html: `
          .enhanced-search-screen {
            background: ${theme.appBackground};
            color: ${theme.primaryText};
          }
          .enhanced-search-header {
            border-bottom-color: ${theme.primaryGradient}20;
          }
          .enhanced-search-back-button,
          .enhanced-search-title,
          .enhanced-search-input {
            color: ${theme.primaryText};
          }
          .enhanced-search-input-wrapper {
            background: ${theme.primaryGradient}10;
            border-color: ${theme.primaryGradient}30;
          }
          .enhanced-current-location-button {
            background: ${theme.primaryGradient}10;
            border-color: ${theme.primaryGradient}20;
            color: ${theme.primaryText};
          }
          .enhanced-search-result-item,
          .enhanced-recent-search-item {
            border-color: ${theme.primaryGradient}20;
            color: ${theme.primaryText};
          }
          .enhanced-search-spinner {
            border: 2px solid ${theme.primaryGradient}30;
            border-top: 2px solid ${theme.primaryGradient};
          }
        `,
        }}
      />

      {/* Header */}
      <header className="enhanced-search-header">
        <button
          className="enhanced-search-back-button"
          onClick={onBack}
          aria-label="Go back"
        >
          ‚Üê
        </button>

        <h1 className="enhanced-search-title">Search Location</h1>
      </header>

      {/* Search Input */}
      <div className="enhanced-search-input-container">
        <div className="enhanced-search-input-wrapper">
          <span className="enhanced-search-icon">üîç</span>
          <input
            type="text"
            className="enhanced-search-input"
            value={searchQuery}
            onChange={e => setSearchQuery(e.target.value)}
            placeholder="Search for a city..."
            autoFocus
          />
          {isSearching && <div className="enhanced-search-spinner" />}
        </div>
      </div>

      {/* Error Display */}
      {error && <div className="enhanced-search-error">‚ö†Ô∏è {error}</div>}

      {/* Content */}
      <div className="enhanced-search-content">
        {/* Current Location Button */}
        <button
          className="enhanced-current-location-button"
          onClick={getCurrentLocation}
          disabled={isGettingLocation || locationPermission === 'denied'}
        >
          <span className="enhanced-current-location-icon">
            {(() => {
              if (isGettingLocation) return '‚è≥';
              if (locationPermission === 'denied') return 'üö´';
              return 'ÔøΩ';
            })()}
          </span>
          <div className="enhanced-current-location-text">
            <div className="enhanced-current-location-title">
              {(() => {
                if (isGettingLocation) return 'Getting Location...';
                if (locationPermission === 'denied') return 'Location Blocked';
                return 'Use Current Location';
              })()}
            </div>
            <div className="enhanced-current-location-subtitle">
              {(() => {
                if (isGettingLocation)
                  return 'Please wait while we get your location';
                if (locationPermission === 'denied')
                  return 'Enable location permissions to use this feature';
                if (locationPermission === 'granted')
                  return 'Location access granted - tap to get weather';
                return 'Get weather for your current location';
              })()}
            </div>
          </div>
        </button>

        {/* Location Troubleshooting Tips */}
        {(locationPermission === 'denied' ||
          error?.includes('Location') ||
          error?.includes('permission')) && (
          <div className="enhanced-location-tips">
            <h4 className="enhanced-location-tips-title">
              üìç Location Troubleshooting
            </h4>
            <div className="enhanced-location-tips-content">
              {!locationService.isSecureContext() && (
                <div className="enhanced-tip">
                  üîí Location requires a secure connection (HTTPS)
                </div>
              )}
              <div className="enhanced-tip">
                üåê Make sure location services are enabled in your browser
              </div>
              <div className="enhanced-tip">
                üì± Check that your device has location services turned on
              </div>
              <div className="enhanced-tip">
                üîÑ Try refreshing the page and allowing location access
              </div>
              <div className="enhanced-tip">
                üîç You can also search for your city manually above
              </div>
            </div>
          </div>
        )}

        {/* Search Results */}
        {searchResults.length > 0 && (
          <div className="enhanced-search-section">
            <h3 className="enhanced-search-section-title">Search Results</h3>
            {searchResults.map((result, index) => (
              <button
                key={`${result.place_id}-${index}`}
                className="enhanced-search-result-item"
                onClick={() => handleCitySelection(result)}
              >
                <span className="enhanced-search-result-icon">üèôÔ∏è</span>
                <div className="enhanced-search-result-text">
                  <div className="enhanced-search-result-name">
                    {result.display_name.split(',')[0]}
                  </div>
                  <div className="enhanced-search-result-location">
                    {result.display_name}
                  </div>
                </div>
              </button>
            ))}
          </div>
        )}

        {/* Recent Searches */}
        {recentSearches.length > 0 && (
          <div className="enhanced-search-section">
            <h3 className="enhanced-search-section-title">Recent Searches</h3>
            {recentSearches.map((result, index) => (
              <button
                key={`recent-${result.lat}-${result.lon}-${index}`}
                className="enhanced-recent-search-item"
                onClick={() => handleRecentSelect(result)}
              >
                <span className="enhanced-recent-search-icon">üïí</span>
                <div className="enhanced-recent-search-text">
                  <div className="enhanced-recent-search-name">
                    {result.name}
                  </div>
                  <div className="enhanced-recent-search-location">
                    {result.state ? `${result.state}, ` : ''}
                    {result.country}
                  </div>
                </div>
              </button>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default EnhancedSearchScreen;

