            <button
              onClick={() => offlineStorage.cleanupExpiredCache()}
              style={{
                backgroundColor: 'rgba(59, 130, 246, 0.8)',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '4px 8px',
                fontSize: '10px',
                cursor: 'pointer',
              }}
            >
              Cleanup
            </button>
          </div>
        </>
      )}

      {/* Compact view */}
      {!expanded && (
        <div style={{ fontSize: '10px', opacity: 0.8 }}>
          <div>
            Bundle:{' '}
            {bundleSizeMonitor.formatBundleSize(bundleStats.averageBundleSize)}
          </div>
          <div>Search: {searchStats.averageLatency.toFixed(0)}ms</div>
          <div>Cache: {cacheStats.cachedCities} cities</div>
        </div>
      )}
    </div>
  );
};

export default PerformanceDashboard;

import React, { useEffect, useState } from 'react';
import type {
  AlertPreferences,
  AlertRule,
  WeatherAlert,
} from '../../services/mobile/WeatherAlertManager';
import { WeatherAlertManager } from '../../services/mobile/WeatherAlertManager';
import './WeatherAlertPanel.css';

interface WeatherAlertPanelProps {
  isVisible: boolean;
  onClose: () => void;
  theme: {
    isDark: boolean;
    colors: {
      primary: string;
      text: string;
      background: string;
    };
  };
}

type TabType = 'alerts' | 'rules' | 'settings';

const WeatherAlertPanel: React.FC<WeatherAlertPanelProps> = ({
  isVisible,
  onClose,
}) => {
  const [activeTab, setActiveTab] = useState<TabType>('alerts');
  const [alerts, setAlerts] = useState<WeatherAlert[]>([]);
  const [rules, setRules] = useState<AlertRule[]>([]);
  const [preferences, setPreferences] = useState<AlertPreferences | null>(null);

  const alertManager = WeatherAlertManager.getInstance();

  useEffect(() => {
    if (isVisible) {
      loadData();
    }
  }, [isVisible]);

  const loadData = () => {
    setAlerts(alertManager.getActiveAlerts());
    setRules(alertManager.getAlertRules());
    setPreferences(alertManager.getPreferences());
  };

  const unreadCount = alerts.filter(
    (alert: WeatherAlert) => !alert.isRead,
  ).length;

  const handleMarkAsRead = (alertId: string) => {
    alertManager.markAlertAsRead(alertId);
    loadData();
  };

  const handleDismissAlert = (alertId: string) => {
    alertManager.dismissAlert(alertId);
    loadData();
  };

  const getSeverityIcon = (severity: string): string => {
    if (severity === 'extreme') return 'üö®';
    if (severity === 'severe') return '‚ö†Ô∏è';
    if (severity === 'warning') return '‚ö°';
    return '‚ÑπÔ∏è';
  };

  if (!isVisible) return null;

  return (
    <div
      className="weather-alert-panel"
      role="dialog"
      aria-labelledby="alert-panel-title"
      aria-modal="true"
    >
      {/* Panel Header */}
      <header className="panel-header">
        <h2 id="alert-panel-title">Weather Alerts</h2>
        <button
          className="close-btn"
          onClick={onClose}
          aria-label="Close weather alerts panel"
          type="button"
        >
          √ó
        </button>
      </header>

      {/* Tab Navigation */}
      <div className="panel-tabs">
        <button
          className={`tab-btn ${activeTab === 'alerts' ? 'active' : ''}`}
          onClick={() => setActiveTab('alerts')}
          type="button"
          aria-pressed={activeTab === 'alerts'}
        >
          Alerts
          {unreadCount > 0 && (
            <span
              className="tab-badge"
              aria-label={`${unreadCount} unread alerts`}
            >
              {unreadCount}
            </span>
          )}
        </button>
        <button
          className={`tab-btn ${activeTab === 'rules' ? 'active' : ''}`}
          onClick={() => setActiveTab('rules')}
          type="button"
          aria-pressed={activeTab === 'rules'}
        >
          Rules ({rules.length})
        </button>
        <button
          className={`tab-btn ${activeTab === 'settings' ? 'active' : ''}`}
          onClick={() => setActiveTab('settings')}
          type="button"
          aria-pressed={activeTab === 'settings'}
        >
          Settings
        </button>
      </div>

      {/* Tab Content */}
      <main className="panel-content">
        {/* Alerts Tab */}
        {activeTab === 'alerts' && (
          <section>
            <div className="alert-list-header">
              <h3>
                Active Alerts
                {unreadCount > 0 && (
                  <span
                    className="unread-badge"
                    aria-label={`${unreadCount} unread`}
                  >
                    {unreadCount}
                  </span>
                )}
              </h3>
              {unreadCount > 0 && (
                <button
                  className="mark-all-read-btn"
                  onClick={() => {
                    alerts.forEach((alert: WeatherAlert) => {
                      if (!alert.isRead) {
                        alertManager.markAlertAsRead(alert.id);
                      }
                    });
                    loadData();
                  }}
                  type="button"
                  aria-label="Mark all alerts as read"
                >
                  Mark All Read
                </button>
              )}
            </div>

            {alerts.length === 0 ? (
              <div className="no-alerts">
                <span className="no-alerts-icon" aria-hidden="true">
                  üå§Ô∏è
                </span>
                <p>No weather alerts</p>
                <small>You'll be notified when weather conditions change</small>
              </div>
            ) : (
              <div className="alerts-container">
                {alerts.map((alert: WeatherAlert) => (
                  <article
                    key={`alert-${alert.id}`}
                    className={`alert-item ${!alert.isRead ? 'unread' : ''} ${
                      !alert.isActive ? 'dismissed' : ''
                    } ${alert.severity}`}
                    aria-label={`Weather alert: ${alert.title}`}
                  >
                    <header className="alert-header">
                      <span className="alert-icon" aria-hidden="true">
                        {getSeverityIcon(alert.severity)}
                      </span>
                      <time
                        className="alert-time"
                        dateTime={alert.startTime.toISOString()}
                      >
                        {alert.startTime.toLocaleTimeString()}
                      </time>
                      {!alert.isRead && (
                        <span className="unread-dot" aria-label="Unread"></span>
                      )}
                    </header>

                    <div className="alert-content">
                      <h4>{alert.title}</h4>
                      <p className="alert-description">{alert.description}</p>
                      <p className="alert-location">üìç {alert.location}</p>

                      <div className="alert-actions">
                        {!alert.isRead && (
                          <button
                            className="action-btn mark-read"
                            onClick={() => handleMarkAsRead(alert.id)}
                            type="button"
                            aria-label={`Mark alert "${alert.title}" as read`}
                          >
                            Mark Read
                          </button>
                        )}
                        {alert.isActive && (
                          <button
                            className="action-btn dismiss"
                            onClick={() => handleDismissAlert(alert.id)}
                            type="button"
                            aria-label={`Dismiss alert "${alert.title}"`}
                          >
                            Dismiss
                          </button>
                        )}
                      </div>
                    </div>
                  </article>
                ))}
              </div>
            )}
          </section>
        )}

        {/* Rules Tab */}
        {activeTab === 'rules' && (
          <section>
            <div className="rules-header">
              <h3>Alert Rules</h3>
              <p>
                Alert rules are managed programmatically. Use the mobile app
                settings to configure custom alert conditions.
              </p>
            </div>

            <div className="rules-list">
              {rules.map((rule: AlertRule) => (
                <article
                  key={`rule-${rule.id}`}
                  className={`rule-item ${
                    rule.enabled ? 'enabled' : 'disabled'
                  }`}
                >
                  <header className="rule-header">
                    <span className="rule-icon" aria-hidden="true">
                      {getSeverityIcon(rule.severity)}
                    </span>
                    <h4 className="rule-title">{rule.title}</h4>

                    <div className="rule-controls">
                      <label
                        className="toggle-switch"
                        htmlFor={`rule-toggle-${rule.id}`}
                      >
                        <input
                          id={`rule-toggle-${rule.id}`}
                          type="checkbox"
                          checked={rule.enabled}
                          onChange={e => {
                            alertManager.updateAlertRule(rule.id, {
                              enabled: e.target.checked,
                            });
                            loadData();
                          }}
                          aria-describedby={`rule-status-${rule.id}`}
                        />
                        <span className="toggle-slider"></span>
                        <span className="sr-only">
                          Toggle {rule.title} rule
                        </span>
                      </label>
                      <span id={`rule-status-${rule.id}`} className="sr-only">
                        {rule.enabled ? 'Enabled' : 'Disabled'}
                      </span>
                    </div>
                  </header>

                  <div className="rule-details">
                    <p className="rule-description">{rule.description}</p>
                    <div className="rule-meta">
                      <span className={`severity-badge ${rule.severity}`}>
                        {rule.severity.charAt(0).toUpperCase() +
                          rule.severity.slice(1)}
                      </span>
                      <span className="locations">
                        üìç {rule.locations.length} location(s)
                      </span>
                    </div>
                  </div>
                </article>
              ))}
            </div>
          </section>
        )}

        {/* Settings Tab */}
        {activeTab === 'settings' && preferences && (
          <section>
            <h3>Alert Settings</h3>

            <div className="settings-group">
              <h4>Notifications</h4>

              <div className="setting-item">
                <label className="setting-label" htmlFor="push-notifications">
                  <input
                    id="push-notifications"
                    type="checkbox"
                    checked={preferences.enableNotifications}
                    onChange={e => {
                      alertManager.updatePreferences({
                        enableNotifications: e.target.checked,
                      });
                      loadData();
                    }}
                  />
                  Enable push notifications
                </label>
              </div>

              <div className="setting-item">
                <label className="setting-label" htmlFor="sound-alerts">
                  <input
                    id="sound-alerts"
                    type="checkbox"
                    checked={preferences.enableSounds}
                    onChange={e => {
                      alertManager.updatePreferences({
                        enableSounds: e.target.checked,
                      });
                      loadData();
                    }}
                  />
                  Play sound for alerts
                </label>
              </div>

              <div className="setting-item">
                <label className="setting-label" htmlFor="vibration-alerts">
                  <input
                    id="vibration-alerts"
                    type="checkbox"
                    checked={preferences.enableVibration}
                    onChange={e => {
                      alertManager.updatePreferences({
                        enableVibration: e.target.checked,
                      });
                      loadData();
                    }}
                  />
                  Enable vibration
                </label>
              </div>
            </div>

            <div className="settings-group">
              <h4>Alert Frequency</h4>

              <div className="setting-item">
                <label htmlFor="min-severity">Minimum alert severity:</label>
                <select
                  id="min-severity"
                  className="setting-select"
                  value={preferences.minimumSeverity}
                  onChange={e => {
                    alertManager.updatePreferences({
                      minimumSeverity: e.target.value as
                        | 'info'
                        | 'warning'
                        | 'severe'
                        | 'extreme',
                    });
                    loadData();
                  }}
                >
                  <option value="info">Info</option>
                  <option value="warning">Warning</option>
                  <option value="severe">Severe</option>
                  <option value="extreme">Extreme</option>
                </select>
              </div>

              <div className="setting-item">
                <label htmlFor="max-alerts">Maximum alerts per day:</label>
                <input
                  id="max-alerts"
                  type="number"
                  min="1"
                  max="50"
                  value={preferences.maxAlertsPerDay}
                  onChange={e => {
                    alertManager.updatePreferences({
                      maxAlertsPerDay: parseInt(e.target.value) || 10,
                    });
                    loadData();
                  }}
                />
              </div>
            </div>

            <div className="settings-group">
              <h4>Quiet Hours</h4>

              <div className="quiet-hours">
                <div className="time-input">
                  <label htmlFor="quiet-start">From:</label>
                  <input
                    id="quiet-start"
                    type="time"
                    value={preferences.quietHours.start}
                    onChange={e => {
                      alertManager.updatePreferences({
                        quietHours: {
                          ...preferences.quietHours,
                          start: e.target.value,
                        },
                      });
                      loadData();
                    }}
                  />
                </div>
                <div className="time-input">
                  <label htmlFor="quiet-end">To:</label>
                  <input
                    id="quiet-end"
                    type="time"
                    value={preferences.quietHours.end}
                    onChange={e => {
                      alertManager.updatePreferences({
                        quietHours: {
                          ...preferences.quietHours,
                          end: e.target.value,
                        },
                      });
                      loadData();
                    }}
                  />
                </div>
              </div>
            </div>

            <div className="settings-actions">
              <button
                className="reset-btn"
                onClick={() => {
                  alertManager.resetPreferences();
                  loadData();
                }}
                type="button"
                aria-label="Reset all settings to default"
              >
                Reset to Defaults
              </button>
            </div>
          </section>
        )}
      </main>
    </div>
  );
};

export default WeatherAlertPanel;

/**
 * Enhanced Weather Display Component - August 2025
 *
 * Improved weather display component with:
 * - Better mobile typography and readability
 * - Enhanced contrast and visibility
 * - Improved spacing and layout for small screens
 * - Better accessibility and touch interaction
 * - Adaptive sizing based on screen dimensions
 */

import React from 'react';
import type { ThemeColors } from '../../utils/themeConfig';
import WeatherIcon from '../../utils/weatherIcons';

interface EnhancedWeatherDisplayProps {
  weatherData: {
    temperature: number;
    condition: string;
    location: string;
    humidity: number;
    windSpeed: number;
    pressure: number;
    uvIndex?: number;
    feelsLike: number;
    visibility?: number;
    weatherCode: number;
  };
  theme: ThemeColors;
  className?: string;
}

const EnhancedWeatherDisplay: React.FC<EnhancedWeatherDisplayProps> = ({
  weatherData,
  theme,
  className = '',
}) => {
  const formatWindSpeed = (speed: number): string => {
    return `${Math.round(speed)} mph`;
  };

  const formatPressure = (pressure: number): string => {
    return `${Math.round(pressure)} hPa`;
  };

  const formatVisibility = (visibility: number | undefined): string => {
    if (!visibility) return 'N/A';
    return `${Math.round(visibility)} mi`;
  };

  const formatUVIndex = (uvIndex: number | undefined): string => {
    if (!uvIndex) return 'N/A';
    return uvIndex.toString();
  };

  const getUVIndexLabel = (uvIndex: number | undefined): string => {
    if (!uvIndex) return 'UV Index';
    if (uvIndex <= 2) return 'Low UV';
    if (uvIndex <= 5) return 'Moderate UV';
    if (uvIndex <= 7) return 'High UV';
    if (uvIndex <= 10) return 'Very High UV';
    return 'Extreme UV';
  };

  return (
    <div className={`enhanced-weather-display ${className}`}>
      {/* Main Weather Display */}
      <div className="enhanced-weather-main">
        <p className="enhanced-weather-location">{weatherData.location}</p>

        <div className="enhanced-weather-icon">
          <WeatherIcon
            code={weatherData.weatherCode}
            size={Math.min(window.innerWidth * 0.2, 120)}
            animated={true}
          />
        </div>

        <div className="enhanced-weather-temp-section">
          <h1 className="enhanced-weather-temperature">
            {Math.round(weatherData.temperature)}¬∞F
          </h1>
          <p className="enhanced-weather-condition">{weatherData.condition}</p>
          <p className="enhanced-weather-feels-like">
            Feels like {Math.round(weatherData.feelsLike)}¬∞F
          </p>
        </div>
      </div>

      {/* Weather Metrics Grid */}
      <div className="enhanced-weather-metrics">
        <div className="enhanced-weather-metric">
          <div className="enhanced-weather-metric-value">
            {weatherData.humidity}%
          </div>
          <div className="enhanced-weather-metric-label">Humidity</div>
        </div>

        <div className="enhanced-weather-metric">
          <div className="enhanced-weather-metric-value">
            {formatWindSpeed(weatherData.windSpeed)}
          </div>
          <div className="enhanced-weather-metric-label">Wind Speed</div>
        </div>

        <div className="enhanced-weather-metric">
          <div className="enhanced-weather-metric-value">
            {formatPressure(weatherData.pressure)}
          </div>
          <div className="enhanced-weather-metric-label">Pressure</div>
        </div>

        {weatherData.visibility !== undefined && (
          <div className="enhanced-weather-metric">
            <div className="enhanced-weather-metric-value">
              {formatVisibility(weatherData.visibility)}
            </div>
            <div className="enhanced-weather-metric-label">Visibility</div>
          </div>
        )}

        {weatherData.uvIndex !== undefined && (
          <div className="enhanced-weather-metric">
            <div className="enhanced-weather-metric-value">
              {formatUVIndex(weatherData.uvIndex)}
            </div>
            <div className="enhanced-weather-metric-label">
              {getUVIndexLabel(weatherData.uvIndex)}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EnhancedWeatherDisplay;

/**
 * iOS 26 Main Screen - Cutting-Edge Weather Interface
 *
 * Implements the latest iOS design patterns:
 * - Fluid Island-style notifications
 * - Advanced glassmorphism with depth layers
 * - Smart adaptive layouts with Dynamic Type
 * - Contextual controls and haptic feedback
 * - Live Activities integration
 * - Spatial UI elements with depth
 * - Enhanced accessibility with VoiceOver
 * - Smart content prioritization
 */

import React, { useState } from 'react';
import type { ThemeColors } from '../../utils/themeConfig';
import { ContextMenu } from './iOS26Components';
import WeatherIcon from '../../utils/weatherIcons';

// ============================================================================
// MAIN WEATHER CARD WITH iOS 26 ENHANCEMENTS
// ============================================================================

interface IOS26WeatherCardProps {
  temperature: number;
  weatherCode: number;
  location: string;
  description: string;
  theme: ThemeColors;
  isLoading?: boolean;
  lastUpdated?: string;
  onRefresh?: () => void;
  onLocationTap?: () => void;
}

const IOS26WeatherCard: React.FC<IOS26WeatherCardProps> = ({
  temperature,
  weatherCode,
  location,
  description,
  theme,
  isLoading = false,
  lastUpdated,
  onRefresh,
  onLocationTap,
}) => {
  const [isPressed, setIsPressed] = useState(false);
  const isDark =
    theme.appBackground.includes('28, 28, 30') ||
    theme.appBackground.includes('#1c1c1e');

  const containerStyle: React.CSSProperties = {
    background: isDark
      ? 'linear-gradient(135deg, rgba(44, 44, 46, 0.8) 0%, rgba(28, 28, 30, 0.9) 100%)'
      : 'linear-gradient(135deg, rgba(255, 255, 255, 0.9) 0%, rgba(248, 250, 252, 0.95) 100%)',
    backdropFilter: 'blur(30px)',
    borderRadius: '28px',
    border: `2px solid ${isDark ? 'rgba(255, 255, 255, 0.12)' : 'rgba(0, 0, 0, 0.08)'}`,
    boxShadow: isDark
      ? '0 20px 60px rgba(0, 0, 0, 0.4), inset 0 1px 0 rgba(255, 255, 255, 0.1)'
      : '0 20px 60px rgba(0, 0, 0, 0.1), inset 0 1px 0 rgba(255, 255, 255, 0.8)',
    padding: '32px',
    margin: '20px',
    position: 'relative',
    overflow: 'hidden',
    transform: `scale(${isPressed ? 0.98 : 1})`,
    transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
    userSelect: 'none',
  };

  const backgroundAccentStyle: React.CSSProperties = {
    position: 'absolute',
    top: '-50%',
    right: '-30%',
    width: '200px',
    height: '200px',
    background: isDark
      ? 'radial-gradient(circle, rgba(0, 122, 255, 0.15) 0%, transparent 70%)'
      : 'radial-gradient(circle, rgba(0, 122, 255, 0.08) 0%, transparent 70%)',
    borderRadius: '50%',
    filter: 'blur(40px)',
    pointerEvents: 'none',
  };

  const headerStyle: React.CSSProperties = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    marginBottom: '24px',
  };

  const locationStyle: React.CSSProperties = {
    fontSize: '18px',
    fontWeight: '600',
    color: theme.primaryText,
    margin: 0,
    cursor: onLocationTap ? 'pointer' : 'default',
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
  };

  const temperatureContainerStyle: React.CSSProperties = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '24px',
    margin: '32px 0',
  };

  const temperatureStyle: React.CSSProperties = {
    fontSize: '72px',
    fontWeight: '200',
    color: theme.primaryText,
    margin: 0,
    letterSpacing: '-2px',
    lineHeight: '1',
  };

  const descriptionStyle: React.CSSProperties = {
    fontSize: '20px',
    fontWeight: '500',
    color: theme.secondaryText,
    margin: 0,
    textAlign: 'center',
  };

  const lastUpdatedStyle: React.CSSProperties = {
    fontSize: '14px',
    color: theme.secondaryText,
    margin: '16px 0 0',
    textAlign: 'center',
    opacity: 0.8,
  };

  const contextActions = [
    {
      id: 'refresh',
      title: 'Refresh Weather',
      icon: 'üîÑ',
      onAction: () => onRefresh?.(),
    },
    {
      id: 'location',
      title: 'Change Location',
      icon: 'üìç',
      onAction: () => onLocationTap?.(),
    },
    {
      id: 'share',
      title: 'Share Weather',
      icon: 'üì§',
      onAction: () => {
        if (navigator.share) {
          navigator.share({
            title: `Weather in ${location}`,
            text: `${temperature}¬∞F - ${description}`,
            url: window.location.href,
          });
        }
      },
    },
  ];

  return (
    <ContextMenu actions={contextActions} theme={theme}>
      <article
        style={containerStyle}
        className="ios26-weather-card"
        aria-label={`Weather in ${location}: ${temperature} degrees Fahrenheit, ${description}`}
      >
        <div style={backgroundAccentStyle} />

        <div style={headerStyle}>
          <button
            style={locationStyle}
            onClick={onLocationTap}
            aria-label={`Current location: ${location}. Tap to change location.`}
            onMouseDown={() => setIsPressed(true)}
            onMouseUp={() => setIsPressed(false)}
            onMouseLeave={() => setIsPressed(false)}
          >
            <span>üìç</span>
            <span>{location}</span>
          </button>

          {isLoading && (
            <div style={{ width: '24px', height: '24px' }}>
              <svg
                viewBox="0 0 24 24"
                style={{ animation: 'spin 1s linear infinite' }}
              >
                <circle
                  cx="12"
                  cy="12"
                  r="10"
                  stroke={theme.secondaryText}
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeDasharray="15 10"
                  fill="none"
                />
              </svg>
            </div>
          )}
        </div>

        <div style={temperatureContainerStyle}>
          <div style={{ transform: 'scale(1.2)' }}>
            <WeatherIcon code={weatherCode} size={48} />
          </div>
          <div style={temperatureStyle}>{temperature}¬∞</div>
        </div>

        <div style={descriptionStyle}>{description}</div>

        {lastUpdated && (
          <div style={lastUpdatedStyle}>Updated {lastUpdated}</div>
        )}
      </article>
    </ContextMenu>
  );
};

