    margin: 0,
    display: 'flex',
    alignItems: 'baseline',
    gap: '4px',
  };

  const metricUnitStyle: React.CSSProperties = {
    fontSize: '16px',
    fontWeight: '400',
    color: theme.secondaryText,
  };

  const getTrendIcon = (trend?: 'up' | 'down' | 'stable') => {
    switch (trend) {
      case 'up':
        return '‚ÜóÔ∏è';
      case 'down':
        return '‚ÜòÔ∏è';
      case 'stable':
        return '‚Üí';
