interface IOS26NavigationBarProps {
  title: string;
  theme: ThemeColors;
  leftAction?: {
    icon: string;
    label: string;
    onPress: () => void;
  };
  rightAction?: {
    icon: string;
    label: string;
    onPress: () => void;
  };
  showBackButton?: boolean;
  onBack?: () => void;
}

const IOS26NavigationBar: React.FC<IOS26NavigationBarProps> = ({
  title,
  theme,
  leftAction,
  rightAction,
  showBackButton = false,
  onBack,
}) => {
  const isDark =
    theme.appBackground.includes('28, 28, 30') ||
    theme.appBackground.includes('#1c1c1e');

  const containerStyle: React.CSSProperties = {
    background: isDark ? 'rgba(28, 28, 30, 0.8)' : 'rgba(248, 250, 252, 0.8)',
    backdropFilter: 'blur(30px)',
    borderBottom: `1px solid ${isDark ? 'rgba(255, 255, 255, 0.1)' : 'rgba(0, 0, 0, 0.1)'}`,
    padding: '16px 20px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    minHeight: '60px',
    position: 'sticky',
    top: 0,
    zIndex: 100,
  };

  const titleStyle: React.CSSProperties = {
    fontSize: '20px',
    fontWeight: '600',
    color: theme.primaryText,
    margin: 0,
    textAlign: 'center',
    flex: 1,
  };

  const actionButtonStyle: React.CSSProperties = {
    background: 'transparent',
    border: 'none',
    color: '#007AFF',
    fontSize: '17px',
    fontWeight: '400',
    cursor: 'pointer',
    padding: '8px 12px',
    borderRadius: '8px',
    transition: 'background-color 0.2s ease',
    display: 'flex',
    alignItems: 'center',
    gap: '6px',
    minWidth: '44px',
    minHeight: '44px',
    justifyContent: 'center',
  };

  const backButtonStyle: React.CSSProperties = {
    ...actionButtonStyle,
    color: '#007AFF',
  };

  return (
    <nav
      style={containerStyle}
      className="ios26-navigation-bar"
      role="navigation"
    >
      <div
        style={{ width: '80px', display: 'flex', justifyContent: 'flex-start' }}
      >
        {showBackButton && onBack ? (
          <button
            style={backButtonStyle}
            onClick={() => {
              if (navigator.vibrate) {
                navigator.vibrate(10);
              }
              onBack();
            }}
            aria-label="Go back"
          >
            <span>‚Äπ</span>
            <span>Back</span>
          </button>
        ) : null}
        {!showBackButton && leftAction && (
          <button
            style={actionButtonStyle}
            onClick={() => {
              if (navigator.vibrate) {
                navigator.vibrate(10);
              }
              leftAction.onPress();
            }}
            aria-label={leftAction.label}
          >
            <span>{leftAction.icon}</span>
          </button>
        )}
      </div>

      <h1 style={titleStyle}>{title}</h1>

      <div
        style={{ width: '80px', display: 'flex', justifyContent: 'flex-end' }}
      >
        {rightAction && (
          <button
            style={actionButtonStyle}
            onClick={() => {
              if (navigator.vibrate) {
                navigator.vibrate(10);
              }
              rightAction.onPress();
            }}
            aria-label={rightAction.label}
          >
            <span>{rightAction.icon}</span>
          </button>
        )}
      </div>
    </nav>
  );
};

