/**
 * OpenMeteo API response interfaces
 */
interface HourlyData {
  time: string[];
  temperature_2m: number[];
  weathercode: number[];
  relative_humidity_2m: number[];
  apparent_temperature: number[];
  surface_pressure: number[];
  uv_index: number[];
  visibility: number[];
}

interface DailyData {
  time: string[];
  weathercode: number[];
  temperature_2m_max: number[];
  temperature_2m_min: number[];
  precipitation_sum: number[];
  windspeed_10m_max: number[];
}

/**
 * Maps OpenMeteo weather codes to human-readable descriptions
 * Reference: https://open-meteo.com/en/docs
 */
const getWeatherDescription = (code: number): string => {
  const descriptions: { [key: number]: string } = {
    0: 'clear sky',
    1: 'mainly clear',
    2: 'partly cloudy',
    3: 'overcast',
    45: 'fog',
    48: 'depositing rime fog',
    51: 'light drizzle',
    53: 'moderate drizzle',
    55: 'dense drizzle',
    61: 'light rain',
    63: 'moderate rain',
    65: 'heavy rain',
    71: 'light snow',
    73: 'moderate snow',
    75: 'heavy snow',
    80: 'light rain showers',
    81: 'moderate rain showers',
    82: 'violent rain showers',
    95: 'thunderstorm',
    96: 'thunderstorm with slight hail',
    99: 'thunderstorm with heavy hail',
  };
  return descriptions[code] || 'unknown';
};

/**
 * Maps OpenMeteo weather codes to main weather categories
 * Used for unified weather type compatibility
 */
const getWeatherMainCategory = (code: number): string => {
  if (code === 0 || code === 1) return 'Clear';
  if (code >= 2 && code <= 3) return 'Clouds';
  if (code >= 45 && code <= 48) return 'Mist';
  if (code >= 51 && code <= 67) return 'Rain';
  if (code >= 71 && code <= 77) return 'Snow';
  if (code >= 80 && code <= 82) return 'Rain';
  if (code >= 95 && code <= 99) return 'Thunderstorm';
  return 'Clear';
};

