/**
 * WeatherAlertManager - Phase 5C Implementation
 * Intelligent weather alert system with push notifications and user preferences
 *
 * Features:
 * - Severe weather detection from OpenMeteo data
 * - Customizable alert rules and thresholds
 * - Web push notifications with fallbacks
 * - Location-aware alerting
 * - Alert history and management
 * - Battery-conscious background processing
 */

import { logger } from '../../utils/logger';

// Alert Types and Severity Levels
export type AlertSeverity = 'info' | 'warning' | 'severe' | 'extreme';
export type AlertType =
  | 'temperature'
  | 'precipitation'
  | 'wind'
  | 'storm'
  | 'visibility'
  | 'general';

export interface WeatherAlert {
  id: string;
  type: AlertType;
  severity: AlertSeverity;
  title: string;
  description: string;
  location: string;
  coordinates?: { lat: number; lon: number };
  startTime: Date;
  endTime?: Date;
  isActive: boolean;
  isRead: boolean;
  notificationSent: boolean;
  createdAt: Date;
}

export interface AlertRule {
  id: string;
  type: AlertType;
  enabled: boolean;
  conditions: {
    temperature?: { min?: number; max?: number };
    precipitation?: { threshold: number; duration?: number };
    windSpeed?: { threshold: number };
    visibility?: { threshold: number };
  };
  locations: string[]; // City names or 'current' for GPS location
  timeRange?: { start: string; end: string }; // HH:MM format
  severity: AlertSeverity;
  notificationEnabled: boolean;
  title: string;
  description: string;
  createdAt: Date;
}

export interface AlertPreferences {
  enableNotifications: boolean;
  enableSounds: boolean;
  enableVibration: boolean;
  quietHours: { start: string; end: string };
  minimumSeverity: AlertSeverity;
  locationAlertsEnabled: boolean;
  favoriteLocationAlertsOnly: boolean;
  maxAlertsPerDay: number;
  alertHistoryDays: number;
}

const DEFAULT_ALERT_PREFERENCES: AlertPreferences = {
  enableNotifications: true,
  enableSounds: true,
  enableVibration: true,
  quietHours: { start: '22:00', end: '07:00' },
  minimumSeverity: 'warning',
  locationAlertsEnabled: true,
  favoriteLocationAlertsOnly: false,
  maxAlertsPerDay: 10,
  alertHistoryDays: 7,
};

export class WeatherAlertManager {
  private static instance: WeatherAlertManager;
  private alerts: WeatherAlert[] = [];
  private alertRules: AlertRule[] = [];
  private preferences: AlertPreferences = DEFAULT_ALERT_PREFERENCES;
  private lastAlertCheck: Date = new Date(0);
  private alertCounts: Map<string, number> = new Map(); // Daily alert counts by location
  private notificationPermission: NotificationPermission = 'default';

  constructor() {
    this.loadStoredData();
    this.requestNotificationPermission();
    this.setupDefaultAlertRules();
  }

  static getInstance(): WeatherAlertManager {
    if (!WeatherAlertManager.instance) {
      WeatherAlertManager.instance = new WeatherAlertManager();
    }
    return WeatherAlertManager.instance;
  }

  // ===== Core Alert Processing =====

  /**
   * Process weather data and trigger alerts based on conditions
   */
  async processWeatherData(
    weatherData: any,
    location: string,
    coordinates?: { lat: number; lon: number },
  ): Promise<WeatherAlert[]> {
    try {
      const triggeredAlerts: WeatherAlert[] = [];
      const currentTime = new Date();

      // Skip if within rate limit or quiet hours
      if (!this.shouldProcessAlerts(location, currentTime)) {
        return triggeredAlerts;
      }

      // Process each active alert rule
      for (const rule of this.alertRules.filter(r => r.enabled)) {
        // Check location match
        if (!this.isLocationMatch(rule, location, coordinates)) {
          continue;
        }

        // Check time range
        if (!this.isTimeRangeMatch(rule, currentTime)) {
          continue;
        }

        // Evaluate weather conditions
        const alertTriggered = await this.evaluateAlertConditions(
          rule,
          weatherData,
          location,
        );
        if (alertTriggered) {
          // Check for duplicate recent alerts
          const existingAlert = this.findRecentAlert(rule, location, 60); // 60 minutes
          if (!existingAlert) {
            const alert = this.createAlert(
              rule,
              weatherData,
              location,
              coordinates,
            );
            triggeredAlerts.push(alert);
            this.addAlert(alert);
          }
        }
      }

      // Process severe weather warnings from OpenMeteo
      const severeWeatherAlerts = await this.processSevereWeatherWarnings(
        weatherData,
        location,
        coordinates,
      );
      triggeredAlerts.push(...severeWeatherAlerts);

      // Send notifications for new alerts
      for (const alert of triggeredAlerts) {
        await this.sendNotification(alert);
      }

      this.lastAlertCheck = currentTime;
      this.saveStoredData();

      logger.info(`Processed weather alerts for ${location}`, {
        alertsTriggered: triggeredAlerts.length,
        location,
        timestamp: currentTime.toISOString(),
      });

      return triggeredAlerts;
    } catch (error) {
      logger.error('Error processing weather data for alerts', {
        error,
        location,
      });
      return [];
    }
  }

  /**
   * Evaluate if weather conditions meet alert rule criteria
   */
  private async evaluateAlertConditions(
    rule: AlertRule,
    weatherData: any,
    location: string,
  ): Promise<boolean> {
    try {
      const conditions = rule.conditions;
      const current = weatherData.current_weather || weatherData.current || {};
      const hourly = weatherData.hourly || {};

      // Temperature alerts
      if (conditions.temperature) {
        const temp = current.temperature || current.temperature_2m;
        if (temp !== undefined) {
          if (
            conditions.temperature.min !== undefined &&
            temp < conditions.temperature.min
          ) {
            return true;
          }
          if (
            conditions.temperature.max !== undefined &&
            temp > conditions.temperature.max
          ) {
            return true;
          }
        }
      }

      // Precipitation alerts
      if (conditions.precipitation) {
        const precipitation =
          current.precipitation || hourly.precipitation?.[0] || 0;
        if (precipitation >= conditions.precipitation.threshold) {
          return true;
        }
      }

      // Wind speed alerts
      if (conditions.windSpeed) {
        const windSpeed = current.windspeed || current.wind_speed_10m || 0;
        if (windSpeed >= conditions.windSpeed.threshold) {
          return true;
        }
      }

      // Visibility alerts
      if (conditions.visibility) {
        const visibility = current.visibility || 10000; // Default to good visibility
        if (visibility <= conditions.visibility.threshold) {
          return true;
        }
      }

      return false;
    } catch (error) {
      logger.error('Error evaluating alert conditions', {
        error,
        rule: rule.id,
        location,
      });
      return false;
    }
  }

  /**
   * Process severe weather warnings from OpenMeteo weather codes
   */
  private async processSevereWeatherWarnings(
    weatherData: any,
    location: string,
    coordinates?: { lat: number; lon: number },
  ): Promise<WeatherAlert[]> {
    const severeAlerts: WeatherAlert[] = [];

    try {
      const current = weatherData.current_weather || weatherData.current || {};
      const weatherCode = current.weathercode || current.weather_code;

      if (weatherCode !== undefined) {
        const severity = this.getWeatherCodeSeverity(weatherCode);

        if (severity === 'severe' || severity === 'extreme') {
          const existingAlert = this.findRecentAlert(
            { id: 'severe-weather', type: 'general' } as AlertRule,
            location,
            120, // 2 hours
          );

          if (!existingAlert) {
            const alert: WeatherAlert = {
              id: `severe-${Date.now()}-${location}`,
              type: 'storm',
              severity,
              title: this.getWeatherCodeTitle(weatherCode),
              description: this.getWeatherCodeDescription(weatherCode, current),
              location,
              coordinates,
              startTime: new Date(),
              isActive: true,
              isRead: false,
              notificationSent: false,
              createdAt: new Date(),
            };

            severeAlerts.push(alert);
          }
        }
      }
    } catch (error) {
      logger.error('Error processing severe weather warnings', {
        error,
        location,
      });
    }

    return severeAlerts;
  }

  // ===== Alert Management =====

  /**
   * Add new alert to system
   */
  private addAlert(alert: WeatherAlert): void {
    this.alerts.unshift(alert); // Add to beginning for chronological order

    // Cleanup old alerts based on preferences
    const cutoffDate = new Date();
    cutoffDate.setDate(
      cutoffDate.getDate() - this.preferences.alertHistoryDays,
    );

    this.alerts = this.alerts.filter(a => a.createdAt >= cutoffDate);

    // Update daily count
    const today = new Date().toDateString();
    const todayCount = this.alertCounts.get(today) || 0;
    this.alertCounts.set(today, todayCount + 1);
  }

  /**
   * Create alert from rule and weather data
   */
  private createAlert(
    rule: AlertRule,
    weatherData: any,
    location: string,
    coordinates?: { lat: number; lon: number },
  ): WeatherAlert {
    const current = weatherData.current_weather || weatherData.current || {};

    return {
      id: `${rule.id}-${Date.now()}-${location}`,
      type: rule.type,
      severity: rule.severity,
      title: rule.title,
      description: this.formatAlertDescription(rule, current, location),
      location,
      coordinates,
      startTime: new Date(),
      isActive: true,
      isRead: false,
      notificationSent: false,
      createdAt: new Date(),
    };
  }

  /**
   * Format alert description with current weather data
   */
  private formatAlertDescription(
    rule: AlertRule,
    weatherData: any,
    location: string,
  ): string {
    let description = rule.description;

    // Replace placeholders with actual values
    description = description.replace('{location}', location);
    description = description.replace(
      '{temperature}',
      `${weatherData.temperature || weatherData.temperature_2m || 'N/A'}¬∞F`,
    );
    description = description.replace(
      '{windSpeed}',
      `${weatherData.windspeed || weatherData.wind_speed_10m || 'N/A'} mph`,
    );
    description = description.replace(
      '{time}',
      new Date().toLocaleTimeString(),
    );

    return description;
  }

  // ===== Notification System =====

  /**
   * Send notification for alert
   */
  private async sendNotification(alert: WeatherAlert): Promise<void> {
    try {
      if (!this.preferences.enableNotifications || !alert.notificationSent) {
        return;
      }

      // Check notification permission
      if (this.notificationPermission !== 'granted') {
        logger.warn(
          'Notification permission not granted, skipping notification',
        );
        return;
      }

      // Check if within quiet hours
      if (this.isQuietHours()) {
        logger.info('Within quiet hours, skipping notification', {
          alert: alert.id,
        });
        return;
      }

      // Create notification
      const notification = new Notification(alert.title, {
        body: alert.description,
        icon: '/icons/weather-alert.png',
        badge: '/icons/weather-badge.png',
        tag: `weather-alert-${alert.type}`,
        data: { alertId: alert.id, location: alert.location },
        requireInteraction: alert.severity === 'extreme',
        silent: !this.preferences.enableSounds,
      });

      // Handle notification events
      notification.onclick = () => {
        this.markAlertAsRead(alert.id);
        window.focus();
        notification.close();
      };

      notification.onclose = () => {
        this.markAlertAsRead(alert.id);
      };

      // Vibration for mobile devices
      if (this.preferences.enableVibration && 'vibrate' in navigator) {
        const pattern =
          alert.severity === 'extreme'
            ? [200, 100, 200, 100, 200]
            : [200, 100, 200];
        navigator.vibrate(pattern);
      }

      alert.notificationSent = true;

      logger.info('Notification sent successfully', {
        alertId: alert.id,
        type: alert.type,
        severity: alert.severity,
      });
    } catch (error) {
      logger.error('Error sending notification', { error, alertId: alert.id });
    }
  }

  /**
   * Request notification permission from user
   */
  private async requestNotificationPermission(): Promise<void> {
    try {
      if ('Notification' in window) {
        this.notificationPermission = Notification.permission;

        if (this.notificationPermission === 'default') {
          const permission = await Notification.requestPermission();
          this.notificationPermission = permission;

          logger.info('Notification permission requested', { permission });
        }
      } else {
        logger.warn('Notifications not supported in this browser');
      }
    } catch (error) {
      logger.error('Error requesting notification permission', { error });
    }
  }

  // ===== Helper Methods =====

  /**
   * Check if alerts should be processed based on rate limits and preferences
   */
  private shouldProcessAlerts(location: string, currentTime: Date): boolean {
    // Check daily limit
    const today = currentTime.toDateString();
    const todayCount = this.alertCounts.get(today) || 0;
    if (todayCount >= this.preferences.maxAlertsPerDay) {
      return false;
    }

    // Check if too soon since last check (rate limiting)
    const timeSinceLastCheck =
      currentTime.getTime() - this.lastAlertCheck.getTime();
    if (timeSinceLastCheck < 5 * 60 * 1000) {
      // 5 minutes minimum
      return false;
    }

    return true;
  }

  /**
   * Check if location matches alert rule
   */
  private isLocationMatch(
    rule: AlertRule,
    location: string,
    coordinates?: { lat: number; lon: number },
  ): boolean {
    if (rule.locations.includes('current') && coordinates) {
      return true;
    }

    return rule.locations.some(
      ruleLocation => ruleLocation.toLowerCase() === location.toLowerCase(),
    );
  }

  /**
   * Check if current time is within rule's time range
   */
  private isTimeRangeMatch(rule: AlertRule, currentTime: Date): boolean {
    if (!rule.timeRange) {
      return true;
    }

    const currentHour = currentTime.getHours();
    const currentMinute = currentTime.getMinutes();
    const currentTimeInMinutes = currentHour * 60 + currentMinute;

    const [startHour, startMinute] = rule.timeRange.start
      .split(':')
      .map(Number);
    const [endHour, endMinute] = rule.timeRange.end.split(':').map(Number);

    const startTimeInMinutes = startHour * 60 + startMinute;
    const endTimeInMinutes = endHour * 60 + endMinute;

    if (startTimeInMinutes <= endTimeInMinutes) {
      return (
        currentTimeInMinutes >= startTimeInMinutes &&
        currentTimeInMinutes <= endTimeInMinutes
      );
    } else {
      // Crosses midnight
      return (
        currentTimeInMinutes >= startTimeInMinutes ||
        currentTimeInMinutes <= endTimeInMinutes
      );
    }
  }

  /**
   * Check if current time is within quiet hours
   */
  private isQuietHours(): boolean {
    const currentTime = new Date();
    return !this.isTimeRangeMatch(
      { timeRange: this.preferences.quietHours } as AlertRule,
      currentTime,
    );
  }

  /**
   * Find recent alert matching criteria
   */
  private findRecentAlert(
    rule: { id: string; type: AlertType },
    location: string,
    minutesBack: number,
  ): WeatherAlert | undefined {
    const cutoffTime = new Date();
    cutoffTime.setMinutes(cutoffTime.getMinutes() - minutesBack);

    return this.alerts.find(
      alert =>
        alert.type === rule.type &&
        alert.location === location &&
        alert.createdAt >= cutoffTime,
    );
  }

  /**
   * Get severity level from OpenMeteo weather code
   */
  private getWeatherCodeSeverity(weatherCode: number): AlertSeverity {
    // OpenMeteo weather codes: https://open-meteo.com/en/docs
    if ([95, 96, 97].includes(weatherCode)) return 'extreme'; // Thunderstorms with hail
    if ([71, 73, 75, 77, 85, 86].includes(weatherCode)) return 'severe'; // Heavy snow
    if ([65, 67, 81, 82].includes(weatherCode)) return 'warning'; // Heavy rain
    return 'info';
  }

  /**
   * Get title for weather code
   */
  private getWeatherCodeTitle(weatherCode: number): string {
    const titles: { [key: number]: string } = {
      95: 'Severe Thunderstorm Warning',
      96: 'Thunderstorm with Light Hail',
      97: 'Thunderstorm with Heavy Hail',
      75: 'Heavy Snow Warning',
      77: 'Snow Grains Warning',
      85: 'Heavy Snow Showers',
      86: 'Heavy Snow Showers',
      67: 'Heavy Freezing Rain',
      82: 'Heavy Rain Showers',
    };

    return titles[weatherCode] || 'Severe Weather Alert';
  }

  /**
   * Get description for weather code
   */
  private getWeatherCodeDescription(
    weatherCode: number,
    weatherData: any,
  ): string {
    const temp = weatherData.temperature || weatherData.temperature_2m || 'N/A';
    const wind = weatherData.windspeed || weatherData.wind_speed_10m || 'N/A';

    const descriptions: { [key: number]: string } = {
      95: `Severe thunderstorms in your area. Temperature: ${temp}¬∞F, Wind: ${wind} mph. Stay indoors and avoid travel.`,
      96: `Thunderstorm with light hail reported. Temperature: ${temp}¬∞F. Protect vehicles and stay inside.`,
      97: `Dangerous thunderstorm with heavy hail. Temperature: ${temp}¬∞F, Wind: ${wind} mph. Seek shelter immediately.`,
      75: `Heavy snowfall warning in effect. Temperature: ${temp}¬∞F. Avoid unnecessary travel.`,
      82: `Heavy rain showers expected. Temperature: ${temp}¬∞F. Watch for flooding and reduced visibility.`,
    };

    return (
      descriptions[weatherCode] ||
      `Severe weather conditions detected. Temperature: ${temp}¬∞F, Wind: ${wind} mph.`
    );
  }

  // ===== Public API Methods =====

  /**
   * Get all active alerts
   */
  getActiveAlerts(): WeatherAlert[] {
    return this.alerts.filter(alert => alert.isActive);
  }

  /**
   * Get unread alerts
   */
  getUnreadAlerts(): WeatherAlert[] {
    return this.alerts.filter(alert => !alert.isRead && alert.isActive);
  }

  /**
   * Get alert history
   */
  getAlertHistory(days?: number): WeatherAlert[] {
    if (!days) return this.alerts;

    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - days);

    return this.alerts.filter(alert => alert.createdAt >= cutoffDate);
  }

  /**
   * Mark alert as read
   */
  markAlertAsRead(alertId: string): void {
    const alert = this.alerts.find(a => a.id === alertId);
    if (alert) {
      alert.isRead = true;
      this.saveStoredData();
    }
  }

  /**
   * Mark all alerts as read
   */
  markAllAlertsAsRead(): void {
    this.alerts.forEach(alert => (alert.isRead = true));
    this.saveStoredData();
  }

  /**
   * Dismiss/deactivate alert
   */
  dismissAlert(alertId: string): void {
    const alert = this.alerts.find(a => a.id === alertId);
    if (alert) {
      alert.isActive = false;
      alert.isRead = true;
      this.saveStoredData();
    }
  }

  /**
   * Get alert rules
   */
  getAlertRules(): AlertRule[] {
    return [...this.alertRules];
  }

  /**
   * Add or update alert rule
   */
  setAlertRule(rule: Omit<AlertRule, 'id' | 'createdAt'>): string {
    const ruleId = `rule-${Date.now()}`;
    const fullRule: AlertRule = {
      ...rule,
      id: ruleId,
      createdAt: new Date(),
    };

    this.alertRules.push(fullRule);
    this.saveStoredData();

    return ruleId;
  }

  /**
   * Update existing alert rule
   */
  updateAlertRule(ruleId: string, updates: Partial<AlertRule>): boolean {
    const ruleIndex = this.alertRules.findIndex(r => r.id === ruleId);
    if (ruleIndex >= 0) {
      this.alertRules[ruleIndex] = {
        ...this.alertRules[ruleIndex],
        ...updates,
      };
      this.saveStoredData();
      return true;
    }
    return false;
  }

  /**
   * Delete alert rule
   */
  deleteAlertRule(ruleId: string): boolean {
    const ruleIndex = this.alertRules.findIndex(r => r.id === ruleId);
    if (ruleIndex >= 0) {
      this.alertRules.splice(ruleIndex, 1);
      this.saveStoredData();
      return true;
    }
    return false;
  }

  /**
   * Get alert preferences
   */
  getPreferences(): AlertPreferences {
    return { ...this.preferences };
  }

  /**
   * Update alert preferences
   */
  updatePreferences(updates: Partial<AlertPreferences>): void {
    this.preferences = { ...this.preferences, ...updates };
    this.saveStoredData();
  }

  /**
   * Reset alert preferences to defaults
   */
  resetPreferences(): void {
    this.preferences = { ...DEFAULT_ALERT_PREFERENCES };
    this.saveStoredData();
  }

  // ===== Default Alert Rules Setup =====

  /**
   * Setup default alert rules for new users
   */
  private setupDefaultAlertRules(): void {
    if (this.alertRules.length === 0) {
      const defaultRules: Omit<AlertRule, 'id' | 'createdAt'>[] = [
        {
          type: 'temperature',
          enabled: true,
          conditions: { temperature: { max: 100 } },
          locations: ['current'],
          severity: 'warning',
          notificationEnabled: true,
          title: 'Extreme Heat Warning',
          description:
            'Temperature is extremely high at {temperature} in {location}. Stay hydrated and avoid outdoor activities.',
        },
        {
          type: 'temperature',
          enabled: true,
          conditions: { temperature: { min: 20 } },
          locations: ['current'],
          severity: 'warning',
          notificationEnabled: true,
          title: 'Extreme Cold Warning',
          description:
            'Temperature is dangerously low at {temperature} in {location}. Dress warmly and limit outdoor exposure.',
        },
        {
          type: 'wind',
          enabled: true,
          conditions: { windSpeed: { threshold: 40 } },
          locations: ['current'],
          severity: 'severe',
          notificationEnabled: true,
          title: 'High Wind Warning',
          description:
            'Dangerous wind speeds of {windSpeed} detected in {location}. Secure outdoor items and avoid driving.',
        },
        {
          type: 'storm',
          enabled: true,
          conditions: {},
          locations: ['current'],
          severity: 'extreme',
          notificationEnabled: true,
          title: 'Severe Weather Alert',
          description:
            'Severe weather conditions detected in {location}. Take immediate shelter and stay indoors.',
        },
      ];

      defaultRules.forEach(rule => this.setAlertRule(rule));
    }
  }

  // ===== Data Persistence =====

  /**
   * Load stored data from localStorage
   */
  private loadStoredData(): void {
    try {
      const alertsData = localStorage.getItem('weatherAlerts');
      if (alertsData) {
        const parsed = JSON.parse(alertsData);
        this.alerts = parsed.map((alert: any) => ({
          ...alert,
          startTime: new Date(alert.startTime),
          endTime: alert.endTime ? new Date(alert.endTime) : undefined,
          createdAt: new Date(alert.createdAt),
        }));
      }

      const rulesData = localStorage.getItem('weatherAlertRules');
      if (rulesData) {
        const parsed = JSON.parse(rulesData);
        this.alertRules = parsed.map((rule: any) => ({
          ...rule,
          createdAt: new Date(rule.createdAt),
        }));
      }

      const preferencesData = localStorage.getItem('weatherAlertPreferences');
      if (preferencesData) {
        this.preferences = {
          ...DEFAULT_ALERT_PREFERENCES,
          ...JSON.parse(preferencesData),
        };
      }

      const countsData = localStorage.getItem('weatherAlertCounts');
      if (countsData) {
        this.alertCounts = new Map(JSON.parse(countsData));
      }
    } catch (error) {
      logger.error('Error loading stored alert data', { error });
    }
  }

  /**
   * Save data to localStorage
   */
  private saveStoredData(): void {
    try {
      localStorage.setItem('weatherAlerts', JSON.stringify(this.alerts));
      localStorage.setItem(
        'weatherAlertRules',
        JSON.stringify(this.alertRules),
      );
      localStorage.setItem(
        'weatherAlertPreferences',
        JSON.stringify(this.preferences),
      );
      localStorage.setItem(
        'weatherAlertCounts',
        JSON.stringify(Array.from(this.alertCounts.entries())),
      );
    } catch (error) {
      logger.error('Error saving alert data', { error });
    }
  }

  /**
   * Clear all stored data (for testing/reset)
   */
  clearAllData(): void {
    this.alerts = [];
    this.alertRules = [];
    this.preferences = { ...DEFAULT_ALERT_PREFERENCES };
    this.alertCounts.clear();

    localStorage.removeItem('weatherAlerts');
    localStorage.removeItem('weatherAlertRules');
    localStorage.removeItem('weatherAlertPreferences');
    localStorage.removeItem('weatherAlertCounts');

    this.setupDefaultAlertRules();
  }
}

// Export singleton instance
export const weatherAlertManager = WeatherAlertManager.getInstance();

/**
 * AutoComplete Search Component
 *
 * Provides real-time city search with autocomplete suggestions.
 * Integrates with OpenStreetMap Nominatim for city lookups.
 */

import React, {
  useCallback,
  useEffect,
  useMemo,
  useRef,
  useState,
} from 'react';
import { useHaptic } from './hapticHooks';
import { logError } from './logger';
import type { ThemeColors } from './themeConfig';

interface CityResult {
  display_name: string;
  lat: string;
  lon: string;
  place_id: number;
  type: string;
  address: {
    city?: string;
    town?: string;
    village?: string;
    state?: string;
    country?: string;
    country_code?: string;
  };
}

interface AutoCompleteSearchProps {
  theme: ThemeColors;
  isMobile: boolean;
  onCitySelected: (
    cityName: string,
    latitude: number,
    longitude: number
  ) => void;
  onError?: (error: string) => void;
  disabled?: boolean;
  placeholder?: string;
  initialValue?: string;
  className?: string;
}

const AutoCompleteSearch: React.FC<AutoCompleteSearchProps> = ({
  theme,
  isMobile,
  onCitySelected,
  onError,
  disabled = false,
  placeholder = 'Search for a city...',
  initialValue = '',
  className,
}) => {
  const [query, setQuery] = useState(initialValue);
  const [suggestions, setSuggestions] = useState<CityResult[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const [hasSearched, setHasSearched] = useState(false);

  const inputRef = useRef<HTMLInputElement>(null);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);
  const abortControllerRef = useRef<AbortController | null>(null);

  const haptic = useHaptic();

  // Debounced search function with caching
  const searchCache = useRef<Map<string, CityResult[]>>(new Map());

  const searchCities = useCallback(
    async (searchQuery: string) => {
      if (!searchQuery.trim() || searchQuery.length < 2) {
        setSuggestions([]);
        setIsOpen(false);
        return;
      }

      // Check cache first for instant results
      const cacheKey = searchQuery.toLowerCase().trim();
      if (searchCache.current.has(cacheKey)) {
        const cachedResults = searchCache.current.get(cacheKey)!;
        setSuggestions(cachedResults);
        setIsOpen(cachedResults.length > 0);
        setIsLoading(false);
        return;
      }

      // Cancel previous request
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }

      setIsLoading(true);
      setHasSearched(true);

      try {
        // Create new abort controller for this request
        abortControllerRef.current = new AbortController();

        // Optimized query with better parameters for speed
        const searchParams = new URLSearchParams({
          q: searchQuery,
          format: 'json',
          addressdetails: '1',
          limit: '6', // Reduced from 8 for faster response
          countrycodes: '', // Allow all countries
          'accept-language': 'en', // Force English for consistency
          dedupe: '1', // Remove duplicates server-side
          extratags: '0', // Disable extra tags for speed
          namedetails: '0', // Disable name details for speed
        });

        const response = await fetch(
          `https://nominatim.openstreetmap.org/search?${searchParams}`,
          {
            headers: {
              'User-Agent':
                'Weather-App/1.0 (https://github.com/user/weather-app)',
            },
            signal: abortControllerRef.current.signal,
            // Add timeout for faster failure
            cache: 'default',
          },
        );

        if (!response.ok) {
          throw new Error(`Search failed: ${response.status}`);
        }

        const results: CityResult[] = await response.json();

        // Optimized filtering for better performance and relevance
        const filteredResults = results
          .filter(result => {
            // Quick relevance check - prioritize exact matches
            const searchLower = searchQuery.toLowerCase();
            const displayLower = result.display_name.toLowerCase();

            // Boost exact city name matches
            if (
              result.address?.city?.toLowerCase().includes(searchLower) ||
              result.address?.town?.toLowerCase().includes(searchLower) ||
              result.address?.village?.toLowerCase().includes(searchLower)
            ) {
              return true;
            }

            // Include if display name starts with search term (high relevance)
            if (displayLower.startsWith(searchLower)) {
              return true;
            }

            // Include if any part of display name contains search term
            return displayLower.includes(searchLower);
          })
          // Sort by relevance - exact matches first
          .sort((a, b) => {
            const searchLower = searchQuery.toLowerCase();
            const aCity = (
              a.address?.city ||
              a.address?.town ||
              a.address?.village ||
              ''
            ).toLowerCase();
            const bCity = (
              b.address?.city ||
              b.address?.town ||
              b.address?.village ||
              ''
            ).toLowerCase();

            // Exact city name matches first
            if (aCity === searchLower && bCity !== searchLower) return -1;
            if (bCity === searchLower && aCity !== searchLower) return 1;

            // Starts with search term next
            if (aCity.startsWith(searchLower) && !bCity.startsWith(searchLower))
              return -1;
            if (bCity.startsWith(searchLower) && !aCity.startsWith(searchLower))
              return 1;

            return 0;
          })
          .slice(0, 5); // Reduced to 5 for faster rendering

        // Cache results for future use
        searchCache.current.set(cacheKey, filteredResults);

        // Limit cache size to prevent memory issues
        if (searchCache.current.size > 50) {
          const firstKey = searchCache.current.keys().next().value;
          if (firstKey) {
            searchCache.current.delete(firstKey);
          }
        }

        setSuggestions(filteredResults);
        setIsOpen(filteredResults.length > 0);
        setSelectedIndex(-1);
      } catch (error) {
        if (error instanceof Error && error.name !== 'AbortError') {
          logError('City search error:', error);
          onError?.('Failed to search for cities. Please try again.');
        }
        setSuggestions([]);
        setIsOpen(false);
      } finally {
        setIsLoading(false);
      }
    },
    [onError],
  );

  // Faster debounce for better responsiveness
  useEffect(() => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }

    // Reduce debounce time for snappier response
    const debounceTime = query.length >= 3 ? 150 : 250; // Faster for longer queries

    timeoutRef.current = setTimeout(() => {
      searchCities(query);
    }, debounceTime);

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [query, searchCities]);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as Node;

      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(target) &&
        inputRef.current &&
        !inputRef.current.contains(target)
      ) {
        setIsOpen(false);
        setSelectedIndex(-1);
      }
    };

    // Use capture phase to prevent interference with other click handlers
    document.addEventListener('mousedown', handleClickOutside, true);
    return () =>
      document.removeEventListener('mousedown', handleClickOutside, true);
  }, []);

  // Handle keyboard navigation
  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (!isOpen) {
      if (event.key === 'ArrowDown' && suggestions.length > 0) {
        event.preventDefault();
        setIsOpen(true);
        setSelectedIndex(0);
      }
      return;
    }

    switch (event.key) {
      case 'ArrowDown':
        event.preventDefault();
        setSelectedIndex(prev =>
          prev < suggestions.length - 1 ? prev + 1 : 0,
        );
        break;
      case 'ArrowUp':
        event.preventDefault();
        setSelectedIndex(prev =>
          prev > 0 ? prev - 1 : suggestions.length - 1,
        );
        break;
      case 'Enter':
        event.preventDefault();
        if (selectedIndex >= 0 && suggestions[selectedIndex]) {
          handleSuggestionSelect(suggestions[selectedIndex]);
        } else if (suggestions.length === 1) {
          // Auto-select first suggestion if only one available
          handleSuggestionSelect(suggestions[0]);
        }
        break;
      case 'Escape':
        event.preventDefault();
        setIsOpen(false);
        setSelectedIndex(-1);
        inputRef.current?.blur();
        break;
      case 'Tab':
        // Allow tab to close suggestions
        setIsOpen(false);
        setSelectedIndex(-1);
        break;
    }
  };

  // Format city display name
  const formatCityName = useCallback((result: CityResult): string => {
    const { address } = result;
    const cityName = address.city || address.town || address.village || '';
    const state = address.state || '';
    const country = address.country || '';

    if (cityName && state && country) {
      return `${cityName}, ${state}, ${country}`;
    } else if (cityName && country) {
      return `${cityName}, ${country}`;
    } else if (cityName) {
      return cityName;
    }

    // Fallback to display_name, but clean it up
    return result.display_name.split(',').slice(0, 3).join(', ');
  }, []);

  // Handle suggestion selection
  const handleSuggestionSelect = useCallback(
    (result: CityResult) => {
      const cityName = formatCityName(result);
      const latitude = parseFloat(result.lat);
      const longitude = parseFloat(result.lon);

      setQuery(cityName);
      setIsOpen(false);
      setSelectedIndex(-1);
      setSuggestions([]);

      haptic.buttonConfirm();
      onCitySelected(cityName, latitude, longitude);
    },
    [formatCityName, haptic, onCitySelected],
  );

  // Handle input focus
  const handleFocus = () => {
    if (suggestions.length > 0) {
      setIsOpen(true);
    }
  };

  // Handle input change
  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value;
    setQuery(value);

    if (value.trim() === '') {
      setSuggestions([]);
      setIsOpen(false);
      setHasSearched(false);
    }
  };

  // Memoized styles for performance
  const inputStyle: React.CSSProperties = useMemo(
    () => ({
      width: '100%',
      padding: isMobile ? '14px 16px' : '16px 18px',
      border: `2px solid ${theme.cardBorder}`,
      borderRadius: '16px',
      background: theme.cardBackground,
      color: theme.primaryText,
      fontSize: isMobile ? '16px' : '17px', // 16px prevents zoom on iOS
      fontWeight: '500',
      outline: 'none',
      transition: 'all 0.3s ease',
      backdropFilter: 'blur(10px)',
      opacity: disabled ? 0.6 : 1,
      cursor: disabled ? 'not-allowed' : 'text',
      paddingRight: isLoading ? '50px' : '16px',
    }),
    [theme, isMobile, disabled, isLoading],
  );

  const dropdownStyle: React.CSSProperties = useMemo(
    () => ({
      position: 'absolute',
      top: '100%',
      left: 0,
      right: 0,
      marginTop: '8px',
      background: theme.cardBackground,
      border: `1px solid ${theme.cardBorder}`,
      borderRadius: '16px',
      boxShadow: '0 10px 25px rgba(0, 0, 0, 0.15)',
      backdropFilter: 'blur(20px)',
      zIndex: 1000,
      maxHeight: isMobile ? '60vh' : '300px',
      overflow: 'hidden',
      transform: isOpen
        ? 'translateY(0) scale(1)'
        : 'translateY(-10px) scale(0.95)',
      opacity: isOpen ? 1 : 0,
      visibility: isOpen ? 'visible' : 'hidden',
      transition: 'all 0.2s cubic-bezier(0.16, 1, 0.3, 1)',
    }),
    [theme, isMobile, isOpen],
  );

  return (
    <div style={{ position: 'relative', width: '100%' }} className={className}>
      {/* Search Input */}
      <div style={{ position: 'relative' }}>
        <input
          ref={inputRef}
          type="text"
          value={query}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          disabled={disabled}
          placeholder={placeholder}
          style={inputStyle}
          autoComplete="off"
          spellCheck="false"
          onMouseEnter={e => {
            if (!disabled) {
              const target = e.target as HTMLInputElement;
              target.style.borderColor = theme.weatherCardBorder;
              target.style.backgroundColor = 'rgba(255, 255, 255, 0.05)';
            }
          }}
          onMouseLeave={e => {
            if (!disabled && document.activeElement !== e.target) {
              const target = e.target as HTMLInputElement;
              target.style.borderColor = theme.cardBorder;
              target.style.backgroundColor = theme.cardBackground;
            }
          }}
          onFocus={e => {
            handleFocus();
            const target = e.target as HTMLInputElement;
            target.style.borderColor = theme.weatherCardBorder;
            target.style.backgroundColor = 'rgba(255, 255, 255, 0.05)';
            target.style.boxShadow = '0 0 0 3px rgba(102, 126, 234, 0.1)';
          }}
          onBlur={e => {
            const target = e.target as HTMLInputElement;
            target.style.borderColor = theme.cardBorder;
            target.style.backgroundColor = theme.cardBackground;
            target.style.boxShadow = 'none';
          }}
        />

        {/* Loading Spinner */}
        {isLoading && (
          <div
            style={{
              position: 'absolute',
              right: '16px',
              top: '50%',
              transform: 'translateY(-50%)',
              width: '20px',
              height: '20px',
              border: '2px solid rgba(102, 126, 234, 0.3)',
              borderTop: '2px solid #667eea',
              borderRadius: '50%',
              animation: 'spin 1s linear infinite',
            }}
          />
        )}
      </div>

      {/* Suggestions Dropdown */}
      <div ref={dropdownRef} style={dropdownStyle}>
        {suggestions.length > 0 ? (
          <div style={{ padding: '8px' }}>
            {suggestions.map((suggestion, index) => (
              <button
                key={suggestion.place_id}
                onClick={e => {
                  e.preventDefault();
                  e.stopPropagation();
                  handleSuggestionSelect(suggestion);
                }}
                style={{
                  width: '100%',
                  padding: '12px 16px',
                  border: 'none',
                  background:
                    index === selectedIndex
                      ? 'rgba(102, 126, 234, 0.1)'
                      : 'transparent',
                  color: theme.primaryText,
                  fontSize: '14px',
                  textAlign: 'left',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  transition: 'all 0.2s ease',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                }}
                onMouseEnter={() => setSelectedIndex(index)}
                onMouseLeave={() => setSelectedIndex(-1)}
              >
                <div>
                  <div style={{ fontWeight: '500' }}>
                    {formatCityName(suggestion)}
                  </div>
                  <div
                    style={{
                      fontSize: '12px',
                      color: theme.secondaryText,
                      marginTop: '2px',
                    }}
                  >
                    {suggestion.address.country}
                  </div>
                </div>
                <div
                  style={{
                    fontSize: '12px',
                    color: theme.secondaryText,
                    opacity: 0.7,
                  }}
                >
                  üìç
                </div>
              </button>
            ))}
          </div>
        ) : hasSearched && !isLoading && query.length >= 2 ? (
          <div
            style={{
              padding: '16px',
              textAlign: 'center',
              color: theme.secondaryText,
              fontSize: '14px',
            }}
          >
            No cities found for "{query}"
          </div>
        ) : null}
      </div>

      {/* CSS for spinner animation */}
      <style>{`
        @keyframes spin {
          0% { transform: translateY(-50%) rotate(0deg); }
          100% { transform: translateY(-50%) rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default AutoCompleteSearch;

import type { ReactNode } from 'react';
import React, {
  createContext,
  useCallback,
  useContext,
  useMemo,
  useState,
} from 'react';

// Loading state types for different operations
export type LoadingOperation =
  | 'weatherData'
  | 'forecast'
  | 'location'
  | 'background-refresh'
  | 'search'
  | 'settings';

export interface LoadingState {
  operation: LoadingOperation;
  isLoading: boolean;
  progress?: number; // 0-100 for progress indication
  error?: string;
  retryCount?: number;
}

export interface LoadingContextType {
  loadingStates: Map<LoadingOperation, LoadingState>;
  setLoading: (
    operation: LoadingOperation,
    isLoading: boolean,
    progress?: number
  ) => void;
  setError: (operation: LoadingOperation, error: string) => void;
  clearError: (operation: LoadingOperation) => void;
  retry: (
    operation: LoadingOperation,
    retryFn: () => Promise<void>
  ) => Promise<void>;
  isAnyLoading: boolean;
  getLoadingState: (operation: LoadingOperation) => LoadingState | undefined;
}

const LoadingContext = createContext<LoadingContextType | undefined>(undefined);

export const useLoading = () => {
  const context = useContext(LoadingContext);
  if (!context) {
    throw new Error('useLoading must be used within a LoadingProvider');
  }
  return context;
};

interface LoadingProviderProps {
  children: ReactNode;
}

export const LoadingProvider: React.FC<LoadingProviderProps> = ({
  children,
}) => {
  const [loadingStates, setLoadingStates] = useState<
    Map<LoadingOperation, LoadingState>
  >(new Map());

  const setLoading = useCallback(
    (operation: LoadingOperation, isLoading: boolean, progress?: number) => {
      setLoadingStates(prev => {
        const newStates = new Map(prev);
        const currentState = newStates.get(operation) || {
          operation,
          isLoading: false,
        };

        newStates.set(operation, {
          ...currentState,
          isLoading,
          progress,
          error: isLoading ? undefined : currentState.error, // Clear error when starting new operation
        });

        return newStates;
      });
    },
    [],
  );

  const setError = useCallback((operation: LoadingOperation, error: string) => {
    setLoadingStates(prev => {
      const newStates = new Map(prev);
      const currentState = newStates.get(operation) || {
        operation,
        isLoading: false,
      };

      newStates.set(operation, {
        ...currentState,
        isLoading: false,
        error,
        retryCount: (currentState.retryCount || 0) + 1,
      });

      return newStates;
    });
  }, []);

  const clearError = useCallback((operation: LoadingOperation) => {
    setLoadingStates(prev => {
      const newStates = new Map(prev);
      const currentState = newStates.get(operation);

      if (currentState) {
        newStates.set(operation, {
          ...currentState,
          error: undefined,
          retryCount: 0,
        });
      }

      return newStates;
    });
  }, []);

  const retry = useCallback(
    async (operation: LoadingOperation, retryFn: () => Promise<void>) => {
      const currentState = loadingStates.get(operation);
      const retryCount = currentState?.retryCount || 0;

      // Limit retry attempts
      if (retryCount >= 3) {
        setError(
          operation,
          'Maximum retry attempts reached. Please try again later.',
        );
        return;
      }

      clearError(operation);
      setLoading(operation, true);

      try {
        await retryFn();
        setLoading(operation, false);
      } catch (error) {
        setError(
          operation,
          error instanceof Error ? error.message : 'Operation failed',
        );
      }
    },
    [loadingStates, setLoading, setError, clearError],
  );

  const isAnyLoading = Array.from(loadingStates.values()).some(
    state => state.isLoading,
  );

  const getLoadingState = useCallback(
    (operation: LoadingOperation) => {
      return loadingStates.get(operation);
    },
    [loadingStates],
  );

  const contextValue: LoadingContextType = useMemo(
    () => ({
      loadingStates,
      setLoading,
      setError,
      clearError,
      retry,
      isAnyLoading,
      getLoadingState,
    }),
    [
      loadingStates,
      setLoading,
      setError,
      clearError,
      retry,
      isAnyLoading,
      getLoadingState,
    ],
  );

  return (
    <LoadingContext.Provider value={contextValue}>
      {children}
    </LoadingContext.Provider>
  );
};

// Hook for specific operation loading state
export const useOperationLoading = (operation: LoadingOperation) => {
  const { getLoadingState, setLoading, setError, clearError, retry } =
    useLoading();

  return {
    loadingState: getLoadingState(operation),
    setLoading: (isLoading: boolean, progress?: number) =>
      setLoading(operation, isLoading, progress),
    setError: (error: string) => setError(operation, error),
    clearError: () => clearError(operation),
    retry: (retryFn: () => Promise<void>) => retry(operation, retryFn),
  };
};

/**
 * Nuclear AutoComplete Fix
 * Simple, bulletproof autocomplete that definitely works
 */

import React, { useCallback, useEffect, useRef, useState } from 'react';
import { logError } from './logger';
import './SimpleAutocomplete.css';

interface SimpleAutocompleteProps {
  onCitySelected: (cityName: string, lat: number, lon: number) => void;
  theme: any;
  disabled?: boolean;
  placeholder?: string;
}

interface CityResult {
  display_name: string;
  lat: string;
  lon: string;
  place_id: number;
}

export const SimpleAutocomplete: React.FC<SimpleAutocompleteProps> = ({
  onCitySelected,
  theme,
  disabled = false,
  placeholder = 'Search cities...',
}) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<CityResult[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const timeoutRef = useRef<NodeJS.Timeout | undefined>(undefined);
  const containerRef = useRef<HTMLDivElement>(null);

  // Simple search function
  const searchCities = useCallback(async (searchTerm: string) => {
    if (searchTerm.length < 2) {
      setResults([]);
      setIsOpen(false);
      return;
    }

    setIsLoading(true);

    try {
      const params = new URLSearchParams({
        q: searchTerm,
        format: 'json',
        limit: '5',
        addressdetails: '1',
      });

      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?${params}`,
        {
          headers: {
            'User-Agent': 'WeatherApp/1.0',
          },
        },
      );

      if (response.ok) {
        const data: CityResult[] = await response.json();
        setResults(data.slice(0, 5));
        setIsOpen(data.length > 0);
      }
    } catch (error) {
      logError('Search error:', error);
      setResults([]);
      setIsOpen(false);
    } finally {
      setIsLoading(false);
    }
  }, []);

  // Debounced search
  useEffect(() => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }

    timeoutRef.current = setTimeout(() => {
      searchCities(query);
    }, 300);

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [query, searchCities]);

  // Handle selection
  const handleSelect = useCallback(
    (result: CityResult) => {
      const cityName = result.display_name.split(',')[0];
      setQuery(cityName);
      setIsOpen(false);
      setResults([]);
      onCitySelected(cityName, parseFloat(result.lat), parseFloat(result.lon));
    },
    [onCitySelected],
  );

  // Click outside to close
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        containerRef.current &&
        !containerRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div ref={containerRef} className="simple-autocomplete">
      <input
        type="text"
        value={query}
        onChange={e => setQuery(e.target.value)}
        placeholder={placeholder}
        disabled={disabled}
        className="simple-autocomplete-input"
      />

      {isLoading && <div className="simple-autocomplete-spinner" />}

      {isOpen && results.length > 0 && (
        <div className="simple-autocomplete-dropdown">
          {results.map(result => (
            <button
              key={result.place_id}
              onClick={e => {
                e.preventDefault();
                e.stopPropagation();
                handleSelect(result);
              }}
              className="simple-autocomplete-item"
            >
              {result.display_name.split(',').slice(0, 3).join(', ')}
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

export default SimpleAutocomplete;

/**
 * Background Sync Manager
 * Handles background synchronization of weather data when network is available
 */

interface PendingRequest {
  id: string;
  type: 'weather-update' | 'city-search' | 'location-fetch';
  data: any;
  timestamp: number;
  retryCount: number;
  priority: 'high' | 'medium' | 'low';
}

interface SyncResult {
  success: boolean;
  processed: number;
  failed: number;
  errors: string[];
}

class BackgroundSyncManager {
  private readonly STORAGE_KEY = 'weather-pending-sync';
  private readonly MAX_RETRY_COUNT = 3;
  private readonly RETRY_DELAY_MS = 5000;
  private isProcessing = false;

  /**
   * Queue a request for background sync
   */
  queueRequest(
    type: PendingRequest['type'],
    data: any,
    priority: PendingRequest['priority'] = 'medium',
  ): void {
    const pendingRequests = this.getPendingRequests();

    const request: PendingRequest = {
      id: this.generateRequestId(),
      type,
      data,
      timestamp: Date.now(),
      retryCount: 0,
      priority,
    };

    pendingRequests.push(request);
    this.savePendingRequests(pendingRequests);

    // Try to sync immediately if online
    if (navigator.onLine) {
      this.processPendingRequests();
    }
  }

  /**
   * Process all pending requests
   */
  async processPendingRequests(): Promise<SyncResult> {
    if (this.isProcessing || !navigator.onLine) {
      return {
        success: false,
        processed: 0,
        failed: 0,
        errors: ['Sync already in progress or offline'],
      };
    }

    this.isProcessing = true;
    const pendingRequests = this.getPendingRequests();

    // Sort by priority and timestamp
    const sortedRequests = this.sortRequestsByPriority(pendingRequests);

    let processed = 0;
    let failed = 0;
    const errors: string[] = [];
    const remainingRequests: PendingRequest[] = [];

    for (const request of sortedRequests) {
      try {
        const success = await this.processRequest(request);

        if (success) {
          processed++;
        } else {
          request.retryCount++;

          if (request.retryCount < this.MAX_RETRY_COUNT) {
            remainingRequests.push(request);
          } else {
            failed++;
            errors.push(`Request ${request.id} exceeded retry limit`);
          }
        }
      } catch (error) {
        request.retryCount++;

        if (request.retryCount < this.MAX_RETRY_COUNT) {
          remainingRequests.push(request);
        } else {
          failed++;
          errors.push(
            `Request ${request.id} failed: ${
              error instanceof Error ? error.message : String(error)
            }`,
          );
        }
      }
    }

    // Save remaining requests
    this.savePendingRequests(remainingRequests);
    this.isProcessing = false;

    return {
      success: errors.length === 0,
      processed,
      failed,
      errors,
    };
  }

  /**
   * Process a single request
   */
  private async processRequest(request: PendingRequest): Promise<boolean> {
    const { type, data } = request;

    try {
      switch (type) {
        case 'weather-update':
          return await this.processWeatherUpdate(data);
        case 'city-search':
          return await this.processCitySearch(data);
        case 'location-fetch':
          return await this.processLocationFetch(data);
        default:
          console.warn(`Unknown request type: ${type}`);
          return false;
      }
    } catch (error) {
      console.error(`Failed to process ${type} request:`, error);
      return false;
    }
  }

  /**
   * Process weather update request
   */
  private async processWeatherUpdate(data: {
    cityName: string;
    latitude: number;
    longitude: number;
  }): Promise<boolean> {
    const { cityName, latitude, longitude } = data;

    try {
      const response = await fetch(
        `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&current_weather=true&hourly=temperature_2m,weather_code,relative_humidity_2m,wind_speed_10m&daily=temperature_2m_max,temperature_2m_min,weather_code,precipitation_sum&temperature_unit=fahrenheit&timezone=auto`,
      );

      if (!response.ok) {
        throw new Error(`Weather API error: ${response.status}`);
      }

      const weatherData = await response.json();

      // Cache the data using our offline storage
      const { offlineStorage } = await import('./offlineWeatherStorage');
      await offlineStorage.cacheWeatherData(cityName, weatherData);

      return true;
    } catch (error) {
      console.error('Weather update failed:', error);
      return false;
    }
  }

  /**
   * Process city search request
   */
  private async processCitySearch(data: { query: string }): Promise<boolean> {
    const { query } = data;

    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?q=${encodeURIComponent(
          query,
        )}&format=json&limit=5`,
        {
          headers: {
            'User-Agent': 'Premium Weather App (https://weather.andernet.dev)',
          },
        },
      );

      if (!response.ok) {
        throw new Error(`Geocoding API error: ${response.status}`);
      }

      const results = await response.json();

      // Cache the search results
      if (results.length > 0) {
        const { offlineStorage } = await import('./offlineWeatherStorage');
        await offlineStorage.cacheRecentCity(
          query,
          parseFloat(results[0].lat),
          parseFloat(results[0].lon),
        );
      }

      return true;
    } catch (error) {
      console.error('City search failed:', error);
      return false;
    }
  }

  /**
   * Process location fetch request
   */
  private async processLocationFetch(data: {
    latitude: number;
    longitude: number;
  }): Promise<boolean> {
    const { latitude, longitude } = data;

    try {
      // Reverse geocoding to get city name
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?lat=${latitude}&lon=${longitude}&format=json`,
        {
          headers: {
            'User-Agent': 'Premium Weather App (https://weather.andernet.dev)',
          },
        },
      );

      if (!response.ok) {
        throw new Error(`Reverse geocoding error: ${response.status}`);
      }

      const location = await response.json();
      const cityName =
        location.display_name?.split(',')[0] || 'Current Location';

      // Cache the location
      const { offlineStorage } = await import('./offlineWeatherStorage');
      await offlineStorage.cacheRecentCity(cityName, latitude, longitude);

      return true;
    } catch (error) {
      console.error('Location fetch failed:', error);
      return false;
    }
  }

  /**
   * Sort requests by priority and timestamp
   */
  private sortRequestsByPriority(requests: PendingRequest[]): PendingRequest[] {
    const priorityOrder = { high: 0, medium: 1, low: 2 };

    return requests.sort((a, b) => {
      // First sort by priority
      const priorityDiff =
        priorityOrder[a.priority] - priorityOrder[b.priority];
      if (priorityDiff !== 0) return priorityDiff;

      // Then sort by timestamp (newer first)
      return b.timestamp - a.timestamp;
    });
  }

  /**
   * Get pending requests from storage
   */
  private getPendingRequests(): PendingRequest[] {
    try {
      const stored = localStorage.getItem(this.STORAGE_KEY);
      return stored ? JSON.parse(stored) : [];
    } catch (error) {
      console.error('Failed to load pending requests:', error);
      return [];
    }
  }

  /**
   * Save pending requests to storage
   */
  private savePendingRequests(requests: PendingRequest[]): void {
    try {
      localStorage.setItem(this.STORAGE_KEY, JSON.stringify(requests));
    } catch (error) {
      console.error('Failed to save pending requests:', error);
    }
  }

  /**
   * Generate unique request ID
   */
  private generateRequestId(): string {
    return `sync_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Get sync statistics
   */
  getSyncStats() {
    const pendingRequests = this.getPendingRequests();

    return {
      pendingCount: pendingRequests.length,
      highPriority: pendingRequests.filter(r => r.priority === 'high').length,
      mediumPriority: pendingRequests.filter(r => r.priority === 'medium')
        .length,
      lowPriority: pendingRequests.filter(r => r.priority === 'low').length,
      isProcessing: this.isProcessing,
      oldestRequest:
        pendingRequests.length > 0
          ? Math.min(...pendingRequests.map(r => r.timestamp))
          : null,
    };
  }

  /**
   * Clear all pending requests (for testing/debugging)
   */
  clearPendingRequests(): void {
    localStorage.removeItem(this.STORAGE_KEY);
  }

  /**
   * Register service worker background sync
   */
  async registerBackgroundSync(): Promise<boolean> {
    if (
      'serviceWorker' in navigator &&
      'sync' in window.ServiceWorkerRegistration.prototype
    ) {
      try {
        const registration = await navigator.serviceWorker.ready;
        if ('sync' in registration) {
          await (registration as any).sync.register('weather-background-sync');
        }
        return true;
      } catch (error) {
        console.error('Background sync registration failed:', error);
        return false;
      }
    }
    return false;
  }
}

// Export singleton instance
export const backgroundSyncManager = new BackgroundSyncManager();

/**
 * Bundle Size and Performance Monitor
 * Tracks bundle performance and lazy loading efficiency
 */

interface BundleMetrics {
  totalBundleSize: number;
  loadedChunks: string[];
  lazyLoadedComponents: string[];
  memoryUsage: number;
  timestamp: number;
}

interface LazyLoadMetrics {
  componentName: string;
  loadTime: number;
  chunkSize?: number;
  timestamp: number;
}

class BundleSizeMonitor {
  private metrics: BundleMetrics[] = [];
  private lazyLoadMetrics: LazyLoadMetrics[] = [];
  private readonly STORAGE_KEY = 'weather-bundle-metrics';
  private readonly MAX_METRICS = 50;

  /**
   * Record bundle load metrics
   */
  recordBundleMetrics(): void {
    try {
      const navigationEntries = performance.getEntriesByType(
        'navigation',
      ) as PerformanceNavigationTiming[];
      const resourceEntries = performance.getEntriesByType(
        'resource',
      ) as PerformanceResourceTiming[];

      // Calculate total bundle size from resource entries
      const totalBundleSize = resourceEntries
        .filter(
          entry =>
            entry.name.includes('.js') ||
            entry.name.includes('.css') ||
            entry.name.includes('.wasm'),
        )
        .reduce((total, entry) => {
          // Use transferSize if available, otherwise decodedBodySize
          const size =
            (entry as any).transferSize || (entry as any).decodedBodySize || 0;
          return total + size;
        }, 0);

      // Get loaded chunks from resource entries
      const loadedChunks = resourceEntries
        .filter(entry => entry.name.includes('.js'))
        .map(entry => {
          const url = new URL(entry.name);
          return url.pathname.split('/').pop() || 'unknown';
        });

      // Get memory usage if available
      const memoryUsage = this.getCurrentMemoryUsage();

      const metric: BundleMetrics = {
        totalBundleSize,
        loadedChunks,
        lazyLoadedComponents: [], // Will be populated by component tracking
        memoryUsage,
        timestamp: Date.now(),
      };

      this.metrics.push(metric);
      this.cleanup();
      this.persistMetrics();
    } catch (error) {
      // Silently fail if performance API is not available
    }
  }

  /**
   * Track lazy component loading
   */
  trackLazyComponent(componentName: string): () => void {
    const startTime = performance.now();

    return () => {
      const endTime = performance.now();
      const loadTime = endTime - startTime;

      const metric: LazyLoadMetrics = {
        componentName,
        loadTime,
        timestamp: Date.now(),
      };

      this.lazyLoadMetrics.push(metric);

      // Update latest bundle metrics with lazy component
      if (this.metrics.length > 0) {
        const latest = this.metrics[this.metrics.length - 1];
        latest.lazyLoadedComponents.push(componentName);
      }

      this.cleanup();
      this.persistMetrics();
    };
  }

  /**
   * Get current memory usage
   */
  private getCurrentMemoryUsage(): number {
    try {
      if ('memory' in performance && (performance as any).memory) {
        return (performance as any).memory.usedJSHeapSize;
      }
    } catch (error) {
      // Memory API not available
    }
    return 0;
  }

  /**
   * Cleanup old metrics
   */
  private cleanup(): void {
    if (this.metrics.length > this.MAX_METRICS) {
      this.metrics = this.metrics.slice(-this.MAX_METRICS);
    }

    if (this.lazyLoadMetrics.length > this.MAX_METRICS) {
      this.lazyLoadMetrics = this.lazyLoadMetrics.slice(-this.MAX_METRICS);
    }
  }

  /**
   * Persist metrics to storage
   */
  private persistMetrics(): void {
    try {
      const data = {
        bundleMetrics: this.metrics,
        lazyLoadMetrics: this.lazyLoadMetrics,
      };
      localStorage.setItem(this.STORAGE_KEY, JSON.stringify(data));
    } catch (error) {
      // Storage failed - continue without persistence
    }
  }

  /**
   * Load persisted metrics
   */
  loadPersistedMetrics(): void {
    try {
      const stored = localStorage.getItem(this.STORAGE_KEY);
      if (stored) {
        const data = JSON.parse(stored);
        this.metrics = data.bundleMetrics || [];
        this.lazyLoadMetrics = data.lazyLoadMetrics || [];
      }
    } catch (error) {
      // Failed to load - start fresh
    }
  }

  /**
   * Get bundle statistics
   */
  getBundleStats(): {
    averageBundleSize: number;
    totalLazyComponents: number;
    averageLazyLoadTime: number;
    memoryTrend: Array<{ timestamp: number; memory: number }>;
    largestChunks: string[];
  } {
    if (this.metrics.length === 0) {
      return {
        averageBundleSize: 0,
        totalLazyComponents: 0,
        averageLazyLoadTime: 0,
        memoryTrend: [],
        largestChunks: [],
      };
    }

    // Average bundle size
    const totalSize = this.metrics.reduce(
      (sum, m) => sum + m.totalBundleSize,
      0,
    );
    const averageBundleSize = totalSize / this.metrics.length;

    // Lazy component stats
    const totalLazyComponents = this.lazyLoadMetrics.length;
    const averageLazyLoadTime =
      totalLazyComponents > 0
        ? this.lazyLoadMetrics.reduce((sum, m) => sum + m.loadTime, 0) /
          totalLazyComponents
        : 0;

    // Memory trend
    const memoryTrend = this.metrics.slice(-10).map(m => ({
      timestamp: m.timestamp,
      memory: m.memoryUsage,
    }));

    // Most common chunks (largest bundles)
    const chunkCounts: Record<string, number> = {};
    this.metrics.forEach(m => {
      m.loadedChunks.forEach(chunk => {
        chunkCounts[chunk] = (chunkCounts[chunk] || 0) + 1;
      });
    });

    const largestChunks = Object.entries(chunkCounts)
      .sort(([, a], [, b]) => b - a)
      .slice(0, 5)
      .map(([chunk]) => chunk);

    return {
      averageBundleSize,
      totalLazyComponents,
      averageLazyLoadTime,
      memoryTrend,
      largestChunks,
    };
  }

  /**
   * Format bundle size for display
   */
  formatBundleSize(bytes: number): string {
    if (bytes === 0) return '0 B';

    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));

    return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`;
  }

  /**
   * Check if bundle size is increasing
   */
  isBundleSizeIncreasing(): boolean {
    if (this.metrics.length < 3) return false;

    const recent = this.metrics.slice(-3);
    const [first, second, third] = recent;

    return (
      third.totalBundleSize > second.totalBundleSize &&
      second.totalBundleSize > first.totalBundleSize
    );
  }

  /**
   * Get performance recommendations
   */
  getRecommendations(): string[] {
    const recommendations: string[] = [];
    const stats = this.getBundleStats();

    if (stats.averageBundleSize > 1024 * 1024) {
      // > 1MB
      recommendations.push('Consider code splitting for large bundle size');
    }

    if (stats.averageLazyLoadTime > 1000) {
      // > 1 second
      recommendations.push(
        'Lazy loaded components are taking too long to load',
      );
    }

    if (this.isBundleSizeIncreasing()) {
      recommendations.push('Bundle size is consistently increasing');
    }

    if (stats.memoryTrend.length > 0) {
      const latestMemory =
        stats.memoryTrend[stats.memoryTrend.length - 1].memory;
      if (latestMemory > 50 * 1024 * 1024) {
        // > 50MB
        recommendations.push('High memory usage detected');
      }
    }

    return recommendations;
  }

  /**
   * Clear all metrics
   */
  clearMetrics(): void {
    this.metrics = [];
    this.lazyLoadMetrics = [];
    try {
      localStorage.removeItem(this.STORAGE_KEY);
    } catch (error) {
      // Failed to clear storage
    }
  }
}

// Export singleton instance
export const bundleSizeMonitor = new BundleSizeMonitor();

// Initialize on load
bundleSizeMonitor.loadPersistedMetrics();

// Record initial bundle metrics after page load
if (typeof window !== 'undefined') {
  window.addEventListener('load', () => {
    setTimeout(() => bundleSizeMonitor.recordBundleMetrics(), 1000);
  });
}

export type { BundleMetrics, LazyLoadMetrics };

/**
 * Enhanced Performance Monitoring - Phase 3C
 * Real-time performance tracking for CSS optimization and service worker efficiency
 */

interface PerformanceMetrics {
  cssLoadTimes: Record<string, number>;
  cacheHitRates: Record<string, number>;
  bundleSizes: Record<string, number>;
  renderTimes: {
    firstPaint: number;
    firstContentfulPaint: number;
    largestContentfulPaint: number;
  };
  optimizationStats: {
    cssChunksLoaded: number;
    totalCSSBytes: number;
    serviceWorkerCacheHits: number;
    memoryUsage: number;
  };
}

class EnhancedPerformanceMonitor {
  private metrics: PerformanceMetrics;
  private observer: PerformanceObserver | null = null;
  private startTime: number;

  constructor() {
    this.startTime = performance.now();
    this.metrics = {
      cssLoadTimes: {},
      cacheHitRates: {},
      bundleSizes: {},
      renderTimes: {
        firstPaint: 0,
        firstContentfulPaint: 0,
        largestContentfulPaint: 0,
      },
      optimizationStats: {
        cssChunksLoaded: 0,
        totalCSSBytes: 0,
        serviceWorkerCacheHits: 0,
        memoryUsage: 0,
      },
    };

    this.initializeMonitoring();
  }

  /**
   * Initialize Performance Monitoring
   */
  private initializeMonitoring(): void {
    this.monitorCSSLoading();
    this.monitorRenderTimes();
    this.monitorCacheEfficiency();
    this.monitorMemoryUsage();
    this.setupPeriodicReporting();
  }

  /**
   * Monitor CSS Loading Performance
   */
  private monitorCSSLoading(): void {
    if (!('PerformanceObserver' in window)) return;

    this.observer = new PerformanceObserver(list => {
      list.getEntries().forEach(entry => {
        if (entry.name.includes('.css')) {
          const fileName = this.extractFileName(entry.name);
          this.metrics.cssLoadTimes[fileName] = entry.duration;

          // Track if it was served from cache
          const wasFromCache = (entry as any).transferSize === 0;
          if (wasFromCache) {
            this.metrics.optimizationStats.serviceWorkerCacheHits++;
          }

          // Track bundle size
          if ((entry as any).transferSize > 0) {
            this.metrics.bundleSizes[fileName] = (entry as any).transferSize;
            this.metrics.optimizationStats.totalCSSBytes += (
              entry as any
            ).transferSize;
          }

          this.logCSSLoadEvent(fileName, entry.duration, wasFromCache);
        }
      });
    });

    this.observer.observe({ entryTypes: ['resource'] });
  }

  /**
   * Monitor Core Web Vitals and Render Times
   */
  private monitorRenderTimes(): void {
    // First Paint and First Contentful Paint
    if ('PerformanceObserver' in window) {
      const paintObserver = new PerformanceObserver(list => {
        list.getEntries().forEach(entry => {
          switch (entry.name) {
            case 'first-paint':
              this.metrics.renderTimes.firstPaint = entry.startTime;
              break;
            case 'first-contentful-paint':
              this.metrics.renderTimes.firstContentfulPaint = entry.startTime;
              break;
          }
        });
      });
      paintObserver.observe({ entryTypes: ['paint'] });

      // Largest Contentful Paint
      const lcpObserver = new PerformanceObserver(list => {
        const entries = list.getEntries();
        const lastEntry = entries[entries.length - 1];
        this.metrics.renderTimes.largestContentfulPaint = lastEntry.startTime;
      });
      lcpObserver.observe({ entryTypes: ['largest-contentful-paint'] });
    }
  }

  /**
   * Monitor Cache Efficiency
   */
  private monitorCacheEfficiency(): void {
    let cacheHits = 0;
    let totalRequests = 0;

    if ('PerformanceObserver' in window) {
      const cacheObserver = new PerformanceObserver(list => {
        list.getEntries().forEach(entry => {
          totalRequests++;

          // Check if request was served from cache
          if ((entry as any).transferSize === 0) {
            cacheHits++;
          }

          // Update cache hit rate
          const hitRate =
            totalRequests > 0 ? (cacheHits / totalRequests) * 100 : 0;
          this.metrics.cacheHitRates.overall = hitRate;
        });
      });
      cacheObserver.observe({ entryTypes: ['resource'] });
    }
  }

  /**
   * Monitor Memory Usage
   */
  private monitorMemoryUsage(): void {
    if ('memory' in performance) {
      setInterval(() => {
        const memory = (performance as any).memory;
        this.metrics.optimizationStats.memoryUsage = memory.usedJSHeapSize;
      }, 10000); // Every 10 seconds
    }
  }

  /**
   * Setup Periodic Performance Reporting
   */
  private setupPeriodicReporting(): void {
    // Report every 30 seconds
    setInterval(() => {
      this.generatePerformanceReport();
    }, 30000);

    // Report on page visibility change
    document.addEventListener('visibilitychange', () => {
      if (document.visibilityState === 'hidden') {
        this.generatePerformanceReport();
      }
    });
  }

  /**
   * Track CSS Chunk Loading
   */
  public trackCSSChunkLoad(
    chunkName: string,
    loadTime: number,
    fromCache: boolean,
  ): void {
    this.metrics.cssLoadTimes[chunkName] = loadTime;
    this.metrics.optimizationStats.cssChunksLoaded++;

    if (fromCache) {
      this.metrics.optimizationStats.serviceWorkerCacheHits++;
    }

    this.logCSSLoadEvent(chunkName, loadTime, fromCache);
  }

  /**
   * Generate Performance Report
   */
  public generatePerformanceReport(): PerformanceReport {
    const totalTime = performance.now() - this.startTime;

    const report: PerformanceReport = {
      timestamp: new Date().toISOString(),
      sessionDuration: totalTime,
      cssOptimization: {
        chunksLoaded: this.metrics.optimizationStats.cssChunksLoaded,
        averageLoadTime: this.calculateAverageLoadTime(),
        totalBytes: this.metrics.optimizationStats.totalCSSBytes,
        cacheHitRate: this.metrics.cacheHitRates.overall || 0,
      },
      renderPerformance: {
        firstPaint: this.metrics.renderTimes.firstPaint,
        firstContentfulPaint: this.metrics.renderTimes.firstContentfulPaint,
        largestContentfulPaint: this.metrics.renderTimes.largestContentfulPaint,
        timeToInteractive: this.calculateTimeToInteractive(),
      },
      memoryUsage: {
        current: this.metrics.optimizationStats.memoryUsage,
        peak: this.calculatePeakMemoryUsage(),
      },
      optimizationEffectiveness: {
        cssReduction: this.calculateCSSReduction(),
        cacheEfficiency: this.metrics.cacheHitRates.overall || 0,
        loadTimeImprovement: this.calculateLoadTimeImprovement(),
      },
    };

    this.reportToAnalytics(report);
    return report;
  }

  /**
   * Calculate Average CSS Load Time
   */
  private calculateAverageLoadTime(): number {
    const loadTimes = Object.values(this.metrics.cssLoadTimes);
    return loadTimes.length > 0
      ? loadTimes.reduce((sum, time) => sum + time, 0) / loadTimes.length
      : 0;
  }

  /**
   * Calculate Time to Interactive
   */
  private calculateTimeToInteractive(): number {
    // Simplified TTI calculation
    return Math.max(
      this.metrics.renderTimes.firstContentfulPaint,
      this.metrics.renderTimes.largestContentfulPaint,
    );
  }

  /**
   * Calculate Peak Memory Usage
   */
  private calculatePeakMemoryUsage(): number {
    // This would be tracked over time in a real implementation
    return this.metrics.optimizationStats.memoryUsage;
  }

  /**
   * Calculate CSS Bundle Size Reduction
   */
  private calculateCSSReduction(): number {
    // Compare against baseline of 127KB (pre-optimization)
    const baselineSize = 127 * 1024; // 127KB
    const currentSize = this.metrics.optimizationStats.totalCSSBytes;

    if (currentSize === 0) return 0;
    return ((baselineSize - currentSize) / baselineSize) * 100;
  }

  /**
   * Calculate Load Time Improvement
   */
  private calculateLoadTimeImprovement(): number {
    // This would compare against historical data
    const averageLoadTime = this.calculateAverageLoadTime();
    const baselineLoadTime = 500; // Assumed baseline in ms

    if (averageLoadTime === 0) return 0;
    return ((baselineLoadTime - averageLoadTime) / baselineLoadTime) * 100;
  }

  /**
   * Extract filename from URL
   */
  private extractFileName(url: string): string {
    return url.split('/').pop() || 'unknown';
  }

  /**
   * Log CSS Load Event
   */
  private logCSSLoadEvent(
    fileName: string,
    duration: number,
    fromCache: boolean,
  ): void {
    // eslint-disable-next-line no-console
    console.log(
      `üìä CSS Load: ${fileName} - ${duration.toFixed(2)}ms ${
        fromCache ? '(cached)' : '(network)'
      }`,
    );
  }

  /**
   * Report to Analytics
   */
  private reportToAnalytics(report: PerformanceReport): void {
    // Send to Dash0 if available
    if (window.dash0?.track) {
      window.dash0.track('performance_report', {
        css_optimization: report.cssOptimization,
        render_performance: report.renderPerformance,
        memory_usage: report.memoryUsage,
      });
    }

    // Log summary for development
    // eslint-disable-next-line no-console
    console.log('üìà Performance Report:', {
      cssChunks: report.cssOptimization.chunksLoaded,
      avgLoadTime: `${report.cssOptimization.averageLoadTime.toFixed(2)}ms`,
      cacheHitRate: `${report.cssOptimization.cacheHitRate.toFixed(1)}%`,
      fcp: `${report.renderPerformance.firstContentfulPaint.toFixed(2)}ms`,
    });
  }

  /**
   * Get Real-time Metrics
   */
  public getCurrentMetrics(): PerformanceMetrics {
    return { ...this.metrics };
  }

  /**
   * Reset Metrics
   */
  public resetMetrics(): void {
    this.startTime = performance.now();
    this.metrics = {
      cssLoadTimes: {},
      cacheHitRates: {},
      bundleSizes: {},
      renderTimes: {
        firstPaint: 0,
        firstContentfulPaint: 0,
        largestContentfulPaint: 0,
      },
      optimizationStats: {
        cssChunksLoaded: 0,
        totalCSSBytes: 0,
        serviceWorkerCacheHits: 0,
        memoryUsage: 0,
      },
    };
  }

  /**
   * Cleanup
   */
  public cleanup(): void {
    if (this.observer) {
      this.observer.disconnect();
    }
  }
}

// Types
interface PerformanceReport {
  timestamp: string;
  sessionDuration: number;
  cssOptimization: {
    chunksLoaded: number;
    averageLoadTime: number;
    totalBytes: number;
    cacheHitRate: number;
  };
  renderPerformance: {
    firstPaint: number;
    firstContentfulPaint: number;
    largestContentfulPaint: number;
    timeToInteractive: number;
  };
  memoryUsage: {
    current: number;
    peak: number;
  };
  optimizationEffectiveness: {
    cssReduction: number;
    cacheEfficiency: number;
    loadTimeImprovement: number;
  };
}

