export type {
  CircuitBreakerState,
  NetworkMetrics,
  NetworkRequest,
  QueuedRequest,
};

/**
 * PWA Offline Storage Enhancement
 * Provides comprehensive offline weather data management
 */

interface WeatherCacheData {
  city: string;
  weather: any;
  timestamp: number;
  expires: number;
}

interface CachedCity {
  name: string;
  lat: number;
  lon: number;
  lastUpdated: number;
}

export class OfflineWeatherStorage {
  private readonly CACHE_KEY = 'weather-app-offline-data';
  private readonly CITIES_KEY = 'weather-app-cities';
  private readonly CACHE_DURATION = 30 * 60 * 1000; // 30 minutes

  /**
   * Cache weather data for offline access
   */
  async cacheWeatherData(city: string, weatherData: any): Promise<void> {
    try {
      const cacheData: WeatherCacheData = {
        city,
        weather: weatherData,
        timestamp: Date.now(),
        expires: Date.now() + this.CACHE_DURATION,
      };

      const existingData = this.getOfflineData();
      existingData[city] = cacheData;

      localStorage.setItem(this.CACHE_KEY, JSON.stringify(existingData));
      console.log(`üóÑÔ∏è Cached weather data for ${city}`);
    } catch (error) {
      console.warn('‚ùå Failed to cache weather data:', error);
    }
  }

  /**
   * Retrieve cached weather data
   */
  getCachedWeatherData(city: string): any | null {
    try {
      const data = this.getOfflineData();
      const cityData = data[city];

      if (!cityData) return null;

      // Check if data is still valid
      if (Date.now() > cityData.expires) {
        delete data[city];
        localStorage.setItem(this.CACHE_KEY, JSON.stringify(data));
        return null;
      }

      console.log(`üì± Retrieved cached weather data for ${city}`);
      return cityData.weather;
    } catch (error) {
      console.warn('‚ùå Failed to retrieve cached weather data:', error);
      return null;
    }
  }

  /**
   * Get all cached offline data
   */
  private getOfflineData(): Record<string, WeatherCacheData> {
    try {
      const data = localStorage.getItem(this.CACHE_KEY);
      return data ? JSON.parse(data) : {};
    } catch {
      return {};
    }
  }

  /**
   * Cache recent city searches
   */
  async cacheRecentCity(name: string, lat: number, lon: number): Promise<void> {
    try {
      const cities = this.getRecentCities();
      const newCity: CachedCity = { name, lat, lon, lastUpdated: Date.now() };

      // Remove existing entry and add to beginning
      const filteredCities = cities.filter(c => c.name !== name);
      const updatedCities = [newCity, ...filteredCities].slice(0, 10); // Keep last 10

      localStorage.setItem(this.CITIES_KEY, JSON.stringify(updatedCities));
    } catch (error) {
      console.warn('‚ùå Failed to cache recent city:', error);
    }
  }

  /**
   * Get recent cities for offline access
   */
  getRecentCities(): CachedCity[] {
    try {
      const data = localStorage.getItem(this.CITIES_KEY);
      return data ? JSON.parse(data) : [];
    } catch {
      return [];
    }
  }

  /**
   * Clear expired cache entries
   */
  cleanupExpiredCache(): void {
    try {
      const data = this.getOfflineData();
      const now = Date.now();
      let cleaned = false;

      Object.keys(data).forEach(city => {
        if (now > data[city].expires) {
          delete data[city];
          cleaned = true;
        }
      });

      if (cleaned) {
        localStorage.setItem(this.CACHE_KEY, JSON.stringify(data));
        console.log('üßπ Cleaned up expired weather cache');
      }
    } catch (error) {
      console.warn('‚ùå Failed to cleanup cache:', error);
    }
  }

  /**
   * Get cache statistics
   */
  getCacheStats(): {
    cachedCities: number;
    recentCities: number;
    totalSize: string;
    oldestCache: string | null;
  } {
    try {
      const weatherData = this.getOfflineData();
      const cities = this.getRecentCities();

      const weatherDataSize = JSON.stringify(weatherData).length;
      const citiesSize = JSON.stringify(cities).length;
      const totalBytes = weatherDataSize + citiesSize;

      const oldestEntry = Object.values(weatherData).sort(
        (a, b) => a.timestamp - b.timestamp,
      )[0];

      return {
        cachedCities: Object.keys(weatherData).length,
        recentCities: cities.length,
        totalSize: `${(totalBytes / 1024).toFixed(1)} KB`,
        oldestCache: oldestEntry
          ? new Date(oldestEntry.timestamp).toLocaleString()
          : null,
      };
    } catch {
      return {
        cachedCities: 0,
        recentCities: 0,
        totalSize: '0 KB',
        oldestCache: null,
      };
    }
  }

  /**
   * Clear all offline data
   */
  clearAllCache(): void {
    try {
      localStorage.removeItem(this.CACHE_KEY);
      localStorage.removeItem(this.CITIES_KEY);
      console.log('üóëÔ∏è Cleared all offline weather data');
    } catch (error) {
      console.warn('‚ùå Failed to clear cache:', error);
    }
  }
}

// Export singleton instance
export const offlineStorage = new OfflineWeatherStorage();

/**
 * Performance Monitor for Search Enhancement Features
 *
 * Provides comprehensive performance tracking, analysis, and optimization
 * for all search-related functionality including memory management.
 */

// Performance Configuration
const PERFORMANCE_CONFIG = {
  // Sampling rates
  SAMPLING_RATE: 0.1, // 10% sampling for production
  DEBUG_SAMPLING_RATE: 1.0, // 100% sampling for development

  // Thresholds (in milliseconds)
  THRESHOLDS: {
    SEARCH_QUERY: 500, // Search should complete under 500ms
    AUTOCORRECT: 100, // Autocorrect should complete under 100ms
    VOICE_RECOGNITION: 2000, // Voice recognition timeout
    CACHE_RETRIEVAL: 50, // Cache access should be under 50ms
    API_REQUEST: 5000, // API requests timeout
    MEMORY_USAGE: 50 * 1024 * 1024, // 50MB memory limit
  },

  // Collection intervals
  COLLECTION_INTERVAL: 30000, // Collect metrics every 30 seconds
  CLEANUP_INTERVAL: 300000, // Cleanup old metrics every 5 minutes
  REPORT_INTERVAL: 60000, // Send performance reports every minute

  // Storage limits
  MAX_METRICS_STORED: 1000,
  MAX_MEMORY_SAMPLES: 100,
  METRICS_TTL: 24 * 60 * 60 * 1000, // 24 hours
} as const;

// Performance Metric Types
interface PerformanceMetric {
  id: string;
  type: string;
  name: string;
  value: number;
  timestamp: number;
  details?: Record<string, unknown>;
  tags?: string[];
}

interface MemoryMetric {
  timestamp: number;
  used: number;
  total: number;
  limit: number;
  heapUsed?: number;
  heapTotal?: number;
  external?: number;
}

interface SearchPerformanceData {
  query: string;
  duration: number;
  source: 'api' | 'cache' | 'popular' | 'autocorrect' | 'voice';
  resultCount: number;
  cacheHit: boolean;
  accuracy?: number;
  confidence?: number;
}

interface PerformanceSummary {
  averageSearchTime: number;
  cacheHitRate: number;
  memoryUsage: MemoryMetric;
  errorRate: number;
  totalSearches: number;
  performanceScore: number;
  recommendations: string[];
}

/**
 * Advanced Performance Monitor
 * Tracks and analyzes all search enhancement performance metrics
 */
export class PerformanceMonitor {
  private metrics: PerformanceMetric[] = [];
  private memoryMetrics: MemoryMetric[] = [];
  private isCollecting = false;
  private collectionTimer: NodeJS.Timeout | null = null;
  private cleanupTimer: NodeJS.Timeout | null = null;
  private reportTimer: NodeJS.Timeout | null = null;
  private observer: PerformanceObserver | null = null;
  private memoryObserver: NodeJS.Timeout | null = null;

  // Performance tracking state
  private activeOperations = new Map<string, number>();
  private searchCache = new Map<string, SearchPerformanceData>();
  private errorCount = 0;
  private totalOperations = 0;

  /**
   * Initialize performance monitoring
   */
  initialize(): void {
    if (this.isCollecting) return;

    try {
      this.setupPerformanceObserver();
      this.setupMemoryMonitoring();
      this.startPeriodicCollection();
      this.isCollecting = true;

      console.log('üìä Performance Monitor initialized successfully');
    } catch (error) {
      console.error('‚ùå Failed to initialize Performance Monitor:', error);
    }
  }

  /**
   * Setup Performance Observer for browser performance metrics
   */
  private setupPerformanceObserver(): void {
    if (typeof PerformanceObserver === 'undefined') {
      console.warn('PerformanceObserver not supported in this environment');
      return;
    }

    try {
      this.observer = new PerformanceObserver(list => {
        const entries = list.getEntries();

        entries.forEach(entry => {
          if (this.shouldSample()) {
            this.recordMetric({
              id: this.generateId(),
              type: 'performance',
              name: entry.name,
              value: entry.duration,
              timestamp: Date.now(),
              details: {
                entryType: entry.entryType,
                startTime: entry.startTime,
                ...this.extractPerformanceDetails(entry),
              },
              tags: ['browser', entry.entryType],
            });
          }
        });
      });

      // Observe different types of performance entries
      this.observer.observe({
        entryTypes: ['measure', 'navigation', 'resource', 'paint'],
      });
    } catch (error) {
      console.warn('Failed to setup PerformanceObserver:', error);
    }
  }

  /**
   * Setup memory monitoring
   */
  private setupMemoryMonitoring(): void {
    this.memoryObserver = setInterval(() => {
      this.collectMemoryMetrics();
    }, PERFORMANCE_CONFIG.COLLECTION_INTERVAL);
  }

  /**
   * Start periodic metric collection and cleanup
   */
  private startPeriodicCollection(): void {
    // Periodic cleanup
    this.cleanupTimer = setInterval(() => {
      this.cleanupOldMetrics();
    }, PERFORMANCE_CONFIG.CLEANUP_INTERVAL);

    // Periodic reporting
    this.reportTimer = setInterval(() => {
      this.generatePerformanceReport();
    }, PERFORMANCE_CONFIG.REPORT_INTERVAL);
  }

  /**
   * Track search operation start
   */
  startSearchOperation(
    operationId: string,
    type: string,
    details?: Record<string, unknown>,
  ): void {
    const startTime = performance.now();
    this.activeOperations.set(operationId, startTime);

    // Record operation start
    this.recordMetric({
      id: this.generateId(),
      type: 'search_operation_start',
      name: type,
      value: startTime,
      timestamp: Date.now(),
      details: {
        operationId,
        ...details,
      },
      tags: ['search', 'start', type],
    });
  }

  /**
   * Track search operation completion
   */
  endSearchOperation(
    operationId: string,
    type: string,
    data: Partial<SearchPerformanceData>,
  ): number {
    const endTime = performance.now();
    const startTime = this.activeOperations.get(operationId);

    if (!startTime) {
      console.warn(`No start time found for operation: ${operationId}`);
      return 0;
    }

    const duration = endTime - startTime;
    this.activeOperations.delete(operationId);
    this.totalOperations++;

    // Record operation completion
    const searchData: SearchPerformanceData = {
      query: data.query || '',
      duration,
      source: data.source || 'api',
      resultCount: data.resultCount || 0,
      cacheHit: data.cacheHit || false,
      accuracy: data.accuracy,
      confidence: data.confidence,
    };

    this.searchCache.set(operationId, searchData);

    this.recordMetric({
      id: this.generateId(),
      type: 'search_operation_complete',
      name: type,
      value: duration,
      timestamp: Date.now(),
      details: {
        operationId,
        ...searchData,
      },
      tags: ['search', 'complete', type, data.source || 'api'],
    });

    // Check performance thresholds
    this.checkPerformanceThreshold(type, duration);

    return duration;
  }

  /**
   * Track search errors
   */
  recordSearchError(
    type: string,
    error: Error,
    details?: Record<string, unknown>,
  ): void {
    this.errorCount++;

    this.recordMetric({
      id: this.generateId(),
      type: 'search_error',
      name: type,
      value: 1,
      timestamp: Date.now(),
      details: {
        error: error.message,
        stack: error.stack,
        ...details,
      },
      tags: ['search', 'error', type],
    });
  }

  /**
   * Track memory usage patterns
   */
  recordMemoryUsage(context: string, details?: Record<string, unknown>): void {
    if ('memory' in performance) {
      const memory = (performance as any).memory;

      this.recordMetric({
        id: this.generateId(),
        type: 'memory_usage',
        name: context,
        value: memory.usedJSHeapSize,
        timestamp: Date.now(),
        details: {
          usedJSHeapSize: memory.usedJSHeapSize,
          totalJSHeapSize: memory.totalJSHeapSize,
          jsHeapSizeLimit: memory.jsHeapSizeLimit,
          ...details,
        },
        tags: ['memory', context],
      });
    }
  }

  /**
   * Collect comprehensive memory metrics
   */
  private collectMemoryMetrics(): void {
    let memoryMetric: MemoryMetric = {
      timestamp: Date.now(),
      used: 0,
      total: 0,
      limit: PERFORMANCE_CONFIG.THRESHOLDS.MEMORY_USAGE,
    };

    // Browser memory API
    if ('memory' in performance) {
      const memory = (performance as any).memory;
      memoryMetric = {
        ...memoryMetric,
        used: memory.usedJSHeapSize,
        total: memory.totalJSHeapSize,
        limit: memory.jsHeapSizeLimit,
        heapUsed: memory.usedJSHeapSize,
        heapTotal: memory.totalJSHeapSize,
      };
    }

    // Estimate memory usage for Node.js environments
    if (typeof process !== 'undefined' && process.memoryUsage) {
      const nodeMemory = process.memoryUsage();
      memoryMetric = {
        ...memoryMetric,
        heapUsed: nodeMemory.heapUsed,
        heapTotal: nodeMemory.heapTotal,
        external: nodeMemory.external,
        used: nodeMemory.heapUsed,
        total: nodeMemory.heapTotal + nodeMemory.external,
      };
    }

    this.memoryMetrics.push(memoryMetric);

    // Limit stored memory metrics
    if (this.memoryMetrics.length > PERFORMANCE_CONFIG.MAX_MEMORY_SAMPLES) {
      this.memoryMetrics = this.memoryMetrics.slice(
        -PERFORMANCE_CONFIG.MAX_MEMORY_SAMPLES,
      );
    }

    // Check memory thresholds
    if (memoryMetric.used > PERFORMANCE_CONFIG.THRESHOLDS.MEMORY_USAGE) {
      this.recordMetric({
        id: this.generateId(),
        type: 'memory_warning',
        name: 'high_memory_usage',
        value: memoryMetric.used,
        timestamp: Date.now(),
        // @ts-expect-error - Type compatibility issue
        details: memoryMetric,
        tags: ['memory', 'warning'],
      });
    }
  }

  /**
   * Record performance metric
   */
  private recordMetric(metric: PerformanceMetric): void {
    this.metrics.push(metric);

    // Limit stored metrics
    if (this.metrics.length > PERFORMANCE_CONFIG.MAX_METRICS_STORED) {
      this.metrics = this.metrics.slice(-PERFORMANCE_CONFIG.MAX_METRICS_STORED);
    }
  }

  /**
   * Check if measurement should be sampled
   */
  private shouldSample(): boolean {
    const rate =
      process.env.NODE_ENV === 'development'
        ? PERFORMANCE_CONFIG.DEBUG_SAMPLING_RATE
        : PERFORMANCE_CONFIG.SAMPLING_RATE;

    return Math.random() < rate;
  }

  /**
   * Check performance threshold violations
   */
  private checkPerformanceThreshold(type: string, duration: number): void {
    const threshold = this.getThresholdForType(type);

    if (threshold && duration > threshold) {
      this.recordMetric({
        id: this.generateId(),
        type: 'performance_warning',
        name: `slow_${type}`,
        value: duration,
        timestamp: Date.now(),
        details: {
          threshold,
          overage: duration - threshold,
        },
        tags: ['performance', 'warning', type],
      });
    }
  }

  /**
   * Get performance threshold for operation type
   */
  private getThresholdForType(type: string): number | null {
    const typeMap: Record<string, keyof typeof PERFORMANCE_CONFIG.THRESHOLDS> =
      {
        search_query: 'SEARCH_QUERY',
        autocorrect: 'AUTOCORRECT',
        voice_recognition: 'VOICE_RECOGNITION',
        cache_retrieval: 'CACHE_RETRIEVAL',
        api_request: 'API_REQUEST',
      };

    const thresholdKey = typeMap[type];
    return thresholdKey ? PERFORMANCE_CONFIG.THRESHOLDS[thresholdKey] : null;
  }

  /**
   * Extract additional details from performance entries
   */
  private extractPerformanceDetails(
    entry: PerformanceEntry,
  ): Record<string, unknown> {
    const details: Record<string, unknown> = {};

    // Navigation timing details
    if (entry.entryType === 'navigation') {
      const navEntry = entry as PerformanceNavigationTiming;
      details.domContentLoaded =
        navEntry.domContentLoadedEventEnd - navEntry.domContentLoadedEventStart;
      details.loadComplete = navEntry.loadEventEnd - navEntry.loadEventStart;
    }

    // Resource timing details
    if (entry.entryType === 'resource') {
      const resourceEntry = entry as PerformanceResourceTiming;
      details.transferSize = resourceEntry.transferSize;
      details.encodedBodySize = resourceEntry.encodedBodySize;
      details.decodedBodySize = resourceEntry.decodedBodySize;
    }

    return details;
  }

  /**
   * Generate performance summary report
   */
  generatePerformanceReport(): PerformanceSummary {
    const searchMetrics = Array.from(this.searchCache.values());
    const latestMemory = this.memoryMetrics[this.memoryMetrics.length - 1] || {
      timestamp: Date.now(),
      used: 0,
      total: 0,
      limit: 0,
    };

    // Calculate averages
    const searchTimes = searchMetrics.map(m => m.duration);
    const averageSearchTime =
      searchTimes.length > 0
        ? searchTimes.reduce((a, b) => a + b, 0) / searchTimes.length
        : 0;

    const cacheHits = searchMetrics.filter(m => m.cacheHit).length;
    const cacheHitRate =
      searchMetrics.length > 0 ? cacheHits / searchMetrics.length : 0;

    const errorRate =
      this.totalOperations > 0 ? this.errorCount / this.totalOperations : 0;

    // Calculate performance score (0-100)
    const performanceScore = this.calculatePerformanceScore({
      averageSearchTime,
      cacheHitRate,
      errorRate,
      memoryUsage: latestMemory.used / latestMemory.limit,
    });

    // Generate recommendations
    const recommendations = this.generateRecommendations({
      averageSearchTime,
      cacheHitRate,
      errorRate,
      memoryUsage: latestMemory.used,
    });

    const summary: PerformanceSummary = {
      averageSearchTime,
      cacheHitRate,
      memoryUsage: latestMemory,
      errorRate,
      totalSearches: searchMetrics.length,
      performanceScore,
      recommendations,
    };

    // Log summary for development
    if (process.env.NODE_ENV === 'development') {
      // eslint-disable-next-line no-console
      console.log('üìä Performance Summary:', summary);
    }

    return summary;
  }

  /**
   * Calculate overall performance score
   */
  private calculatePerformanceScore(metrics: {
    averageSearchTime: number;
    cacheHitRate: number;
    errorRate: number;
    memoryUsage: number;
  }): number {
    let score = 100;

    // Search time penalty (0-30 points)
    const searchTimePenalty = Math.min(
      30,
      (metrics.averageSearchTime / 1000) * 10,
    );
    score -= searchTimePenalty;

    // Cache hit rate bonus (0-20 points)
    const cacheBonus = metrics.cacheHitRate * 20;
    score += cacheBonus - 20; // Subtract 20 to make it penalty-based

    // Error rate penalty (0-25 points)
    const errorPenalty = Math.min(25, metrics.errorRate * 100);
    score -= errorPenalty;

    // Memory usage penalty (0-25 points)
    const memoryPenalty = Math.min(25, metrics.memoryUsage * 25);
    score -= memoryPenalty;

    return Math.max(0, Math.min(100, score));
  }

  /**
   * Generate performance recommendations
   */
  private generateRecommendations(metrics: {
    averageSearchTime: number;
    cacheHitRate: number;
    errorRate: number;
    memoryUsage: number;
  }): string[] {
    const recommendations: string[] = [];

    if (metrics.averageSearchTime > 500) {
      recommendations.push(
        'Consider optimizing search algorithms or implementing better caching',
      );
    }

    if (metrics.cacheHitRate < 0.5) {
      recommendations.push(
        'Improve cache strategy to increase hit rate above 50%',
      );
    }

    if (metrics.errorRate > 0.05) {
      recommendations.push(
        'Investigate and fix search errors (>5% error rate detected)',
      );
    }

    if (
      metrics.memoryUsage >
      PERFORMANCE_CONFIG.THRESHOLDS.MEMORY_USAGE * 0.8
    ) {
      recommendations.push('Monitor memory usage - approaching 80% of limit');
    }

    if (recommendations.length === 0) {
      recommendations.push(
        'Performance is optimal - no immediate improvements needed',
      );
    }

    return recommendations;
  }

  /**
   * Get metrics from recent time period
   */
  private getRecentMetrics(timeWindow: number): PerformanceMetric[] {
    const cutoff = Date.now() - timeWindow;
    return this.metrics.filter(metric => metric.timestamp >= cutoff);
  }

  /**
   * Clean up old metrics
   */
  private cleanupOldMetrics(): void {
    const cutoff = Date.now() - PERFORMANCE_CONFIG.METRICS_TTL;

    this.metrics = this.metrics.filter(metric => metric.timestamp >= cutoff);
    this.memoryMetrics = this.memoryMetrics.filter(
      metric => metric.timestamp >= cutoff,
    );

    // Clear old search cache entries
    const oldOperations = Array.from(this.searchCache.entries())
      .filter(
        ([, data]) =>
          Date.now() - data.duration > PERFORMANCE_CONFIG.METRICS_TTL,
      )
      .map(([id]) => id);

    oldOperations.forEach(id => this.searchCache.delete(id));

    console.log(
      `üßπ Cleaned up ${oldOperations.length} old performance metrics`,
    );
  }

  /**
   * Generate unique identifier
   */
  private generateId(): string {
    return `perf_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Get all collected metrics
   */
  getMetrics(filter?: {
    type?: string;
    timeRange?: { start: number; end: number };
    tags?: string[];
  }): PerformanceMetric[] {
    let filtered = [...this.metrics];

    if (filter?.type) {
      filtered = filtered.filter(m => m.type === filter.type);
    }

    if (filter?.timeRange) {
      filtered = filtered.filter(
        m =>
          m.timestamp >= filter.timeRange!.start &&
          m.timestamp <= filter.timeRange!.end,
      );
    }

    if (filter?.tags && filter.tags.length > 0) {
      filtered = filtered.filter(m =>
        m.tags?.some(tag => filter.tags!.includes(tag)),
      );
    }

    return filtered;
  }

  /**
   * Get memory usage history
   */
  getMemoryHistory(): MemoryMetric[] {
    return [...this.memoryMetrics];
  }

  /**
   * Reset all metrics and counters
   */
  reset(): void {
    this.metrics = [];
    this.memoryMetrics = [];
    this.searchCache.clear();
    this.activeOperations.clear();
    this.errorCount = 0;
    this.totalOperations = 0;

    console.log('üîÑ Performance Monitor reset successfully');
  }

  /**
   * Stop performance monitoring and cleanup
   */
  destroy(): void {
    this.isCollecting = false;

    if (this.observer) {
      this.observer.disconnect();
      this.observer = null;
    }

    if (this.collectionTimer) {
      clearInterval(this.collectionTimer);
      this.collectionTimer = null;
    }

    if (this.cleanupTimer) {
      clearInterval(this.cleanupTimer);
      this.cleanupTimer = null;
    }

    if (this.reportTimer) {
      clearInterval(this.reportTimer);
      this.reportTimer = null;
    }

    if (this.memoryObserver) {
      clearInterval(this.memoryObserver);
      this.memoryObserver = null;
    }

    this.reset();
    console.log('üîå Performance Monitor destroyed');
  }
}

// Export singleton instance
export const performanceMonitor = new PerformanceMonitor();

// Export types for use in other modules
export type {
  MemoryMetric,
  PerformanceMetric,
  PerformanceSummary,
  SearchPerformanceData,
};

/**
 * Advanced Search Cache Manager with IndexedDB
 * Provides persistent offline storage for search results with intelligent caching
 */

/* eslint-disable no-console */

// Cache configuration
const CACHE_CONFIG = {
  DATABASE_NAME: 'WeatherAppSearchCache',
  DATABASE_VERSION: 1,
  STORE_NAME: 'searchResults',
  DEFAULT_TTL: 24 * 60 * 60 * 1000, // 24 hours
  MAX_CACHE_SIZE: 1000,
  CLEANUP_THRESHOLD: 0.8,
} as const;

// Types
interface CachedSearchResult {
  id: string;
  query: string;
  results: unknown[];
  timestamp: number;
  ttl: number;
  source: 'api' | 'autocorrect' | 'prefetch';
  metadata: {
    responseTime: number;
    accuracy?: number;
    queryNormalized?: string;
  };
}

interface CacheMetrics {
  hitRate: number;
  cacheSize: number;
  lastCleanup: number;
  totalRequests: number;
  cacheHits: number;
}

/**
 * Advanced Search Cache Manager
 */
class SearchCacheManager {
  private db: IDBDatabase | null = null;
  private metrics: CacheMetrics = {
    hitRate: 0,
    cacheSize: 0,
    lastCleanup: Date.now(),
    totalRequests: 0,
    cacheHits: 0,
  };

  /**
   * Initialize the cache manager
   */
  async initialize(): Promise<void> {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open(
        CACHE_CONFIG.DATABASE_NAME,
        CACHE_CONFIG.DATABASE_VERSION,
      );

      request.onerror = () => {
        console.error('Failed to open cache database:', request.error);
        reject(request.error);
      };

      request.onsuccess = () => {
        this.db = request.result;
        console.log('‚úÖ Search cache initialized');
        resolve();
      };

      request.onupgradeneeded = event => {
        const db = (event.target as IDBOpenDBRequest).result;

        if (!db.objectStoreNames.contains(CACHE_CONFIG.STORE_NAME)) {
          const store = db.createObjectStore(CACHE_CONFIG.STORE_NAME, {
            keyPath: 'id',
          });

          store.createIndex('query', 'query', { unique: false });
          store.createIndex('timestamp', 'timestamp', { unique: false });
          store.createIndex('source', 'source', { unique: false });
        }
      };
    });
  }

  /**
   * Cache search results
   */
  async cacheSearchResults(
    query: string,
    results: unknown[],
    source: 'api' | 'autocorrect' | 'prefetch',
    metadata: { responseTime: number; accuracy?: number },
  ): Promise<void> {
    if (!this.db) {
      console.warn('Cache not initialized');
      return;
    }

    const normalizedQuery = this.normalizeQuery(query);
    const cacheEntry: CachedSearchResult = {
      id: this.generateCacheKey(normalizedQuery),
      query: normalizedQuery,
      results,
      timestamp: Date.now(),
      ttl: CACHE_CONFIG.DEFAULT_TTL,
      source,
      metadata: {
        ...metadata,
        queryNormalized: normalizedQuery,
      },
    };

    return new Promise((resolve, reject) => {
      const transaction = this.db!.transaction(
        [CACHE_CONFIG.STORE_NAME],
        'readwrite',
      );
      const store = transaction.objectStore(CACHE_CONFIG.STORE_NAME);

      const request = store.put(cacheEntry);

      request.onsuccess = () => {
        this.updateCacheSize();
        resolve();
      };

      request.onerror = () => {
        console.error('Failed to cache results:', request.error);
        reject(request.error);
      };
    });
  }

  /**
   * Get cached search results
   */
  async getCachedResults(query: string): Promise<CachedSearchResult | null> {
    if (!this.db) return null;

    this.metrics.totalRequests++;
    const normalizedQuery = this.normalizeQuery(query);

    return new Promise(resolve => {
      const transaction = this.db!.transaction(
        [CACHE_CONFIG.STORE_NAME],
        'readonly',
      );
      const store = transaction.objectStore(CACHE_CONFIG.STORE_NAME);
      const index = store.index('query');

      const request = index.get(normalizedQuery);

      request.onsuccess = () => {
        const result = request.result as CachedSearchResult | undefined;

        if (result && this.isValidCache(result)) {
          this.metrics.cacheHits++;
          this.updateHitRate();
          resolve(result);
        } else if (result) {
          // Expired cache, remove it
          this.removeCacheEntry(result.id);
          resolve(null);
        } else {
          // Try fuzzy search for typos
          this.findFuzzyMatch(normalizedQuery).then(resolve);
        }
      };

      request.onerror = () => {
        console.error('Failed to get cached results:', request.error);
        resolve(null);
      };
    });
  }

  /**
   * Find fuzzy matches for typos
   */
  private async findFuzzyMatch(
    query: string,
  ): Promise<CachedSearchResult | null> {
    if (!this.db) return null;

    return new Promise(resolve => {
      const transaction = this.db!.transaction(
        [CACHE_CONFIG.STORE_NAME],
        'readonly',
      );
      const store = transaction.objectStore(CACHE_CONFIG.STORE_NAME);

      const request = store.getAll();

      request.onsuccess = () => {
        const allEntries = request.result as CachedSearchResult[];
        let bestMatch: CachedSearchResult | null = null;
        let bestScore = 0;

        for (const entry of allEntries) {
          if (!this.isValidCache(entry)) continue;

          const similarity = this.calculateSimilarity(query, entry.query);
          if (similarity > 0.8 && similarity > bestScore) {
            bestMatch = entry;
            bestScore = similarity;
          }
        }

        if (bestMatch) {
          this.metrics.cacheHits++;
          this.updateHitRate();
        }

        resolve(bestMatch);
      };

      request.onerror = () => {
        resolve(null);
      };
    });
  }

  /**
   * Calculate string similarity using Levenshtein distance
   */
  private calculateSimilarity(str1: string, str2: string): number {
    const matrix: number[][] = [];
    const len1 = str1.length;
    const len2 = str2.length;

    for (let i = 0; i <= len1; i++) {
      matrix[i] = [i];
    }

    for (let j = 0; j <= len2; j++) {
      matrix[0][j] = j;
    }

    for (let i = 1; i <= len1; i++) {
      for (let j = 1; j <= len2; j++) {
        const cost = str1[i - 1] === str2[j - 1] ? 0 : 1;
        matrix[i][j] = Math.min(
          matrix[i - 1][j] + 1,
          matrix[i][j - 1] + 1,
          matrix[i - 1][j - 1] + cost,
        );
      }
    }

    const maxLen = Math.max(len1, len2);
    return (maxLen - matrix[len1][len2]) / maxLen;
  }

  /**
   * Clear cache
   */
  async clearCache(): Promise<void> {
    if (!this.db) return;

    return new Promise((resolve, reject) => {
      const transaction = this.db!.transaction(
        [CACHE_CONFIG.STORE_NAME],
        'readwrite',
      );
      const store = transaction.objectStore(CACHE_CONFIG.STORE_NAME);

      const request = store.clear();

      request.onsuccess = () => {
        this.resetMetrics();
        console.log('üßπ Search cache cleared');
        resolve();
      };

      request.onerror = () => {
        console.error('Failed to clear cache:', request.error);
        reject(request.error);
      };
    });
  }

  /**
   * Get cache metrics
   */
  getCacheMetrics(): CacheMetrics {
    return { ...this.metrics };
  }

  /**
   * Cleanup expired entries
   */
  async cleanupExpiredEntries(): Promise<void> {
    if (!this.db) return;

    const currentTime = Date.now();

    return new Promise((resolve, reject) => {
      const transaction = this.db!.transaction(
        [CACHE_CONFIG.STORE_NAME],
        'readwrite',
      );
      const store = transaction.objectStore(CACHE_CONFIG.STORE_NAME);

      const request = store.getAll();

      request.onsuccess = () => {
        const allEntries = request.result as CachedSearchResult[];
        const expiredEntries = allEntries.filter(
          entry => !this.isValidCache(entry),
        );

        const deletePromises = expiredEntries.map(
          entry =>
            new Promise<void>(deleteResolve => {
              const deleteRequest = store.delete(entry.id);
              deleteRequest.onsuccess = () => deleteResolve();
              deleteRequest.onerror = () => deleteResolve();
            }),
        );

        Promise.all(deletePromises).then(() => {
          this.metrics.lastCleanup = currentTime;
          this.updateCacheSize();
          console.log(`üßπ Cleaned up ${expiredEntries.length} expired entries`);
          resolve();
        });
      };

      request.onerror = () => {
        console.error('Failed to cleanup cache:', request.error);
        reject(request.error);
      };
    });
  }

  // Helper methods
  private normalizeQuery(query: string): string {
    return query.toLowerCase().trim().replace(/\s+/g, ' ');
  }

  private generateCacheKey(query: string): string {
    return `cache_${btoa(encodeURIComponent(query))}_${Date.now()}`;
  }

  private isValidCache(entry: CachedSearchResult): boolean {
    return Date.now() - entry.timestamp < entry.ttl;
  }

  private async updateCacheSize(): Promise<void> {
    if (!this.db) return;

    const transaction = this.db.transaction(
      [CACHE_CONFIG.STORE_NAME],
      'readonly',
    );
    const store = transaction.objectStore(CACHE_CONFIG.STORE_NAME);

    const request = store.count();
    request.onsuccess = () => {
      this.metrics.cacheSize = request.result;

      // Auto-cleanup if threshold exceeded
      if (
        this.metrics.cacheSize >
        CACHE_CONFIG.MAX_CACHE_SIZE * CACHE_CONFIG.CLEANUP_THRESHOLD
      ) {
        this.cleanupExpiredEntries();
      }
    };
  }

  private async removeCacheEntry(id: string): Promise<void> {
    if (!this.db) return;

    const transaction = this.db.transaction(
      [CACHE_CONFIG.STORE_NAME],
      'readwrite',
    );
    const store = transaction.objectStore(CACHE_CONFIG.STORE_NAME);
    store.delete(id);
  }

  private updateHitRate(): void {
    this.metrics.hitRate =
      this.metrics.totalRequests > 0
        ? this.metrics.cacheHits / this.metrics.totalRequests
        : 0;
  }

  private resetMetrics(): void {
    this.metrics = {
      hitRate: 0,
      cacheSize: 0,
      lastCleanup: Date.now(),
      totalRequests: 0,
      cacheHits: 0,
    };
  }
}

// Export singleton instance
export const searchCacheManager = new SearchCacheManager();
export type { CachedSearchResult, CacheMetrics };

/**
 * Search Performance Monitor
 * Tracks search performance metrics and memory usage
 */

interface SearchPerformanceMetrics {
  searchType: string;
  duration: number;
  timestamp: number;
  resultsCount: number;
  memoryUsage?: {
    used: number;
    total: number;
    limit: number;
  };
}

interface PerformanceStats {
  averageLatency: number;
  totalSearches: number;
  searchTypes: Record<string, number>;
  memoryTrend: Array<{ timestamp: number; used: number }>;
}

class SearchPerformanceMonitor {
  private metrics: SearchPerformanceMetrics[] = [];
  private readonly MAX_METRICS = 100; // Keep last 100 searches
  private readonly STORAGE_KEY = 'weather-search-performance';

  /**
   * Start tracking a search operation
   */
  startSearch(searchType: string): string {
    const trackingId = `search-${searchType}-${Date.now()}`;
    performance.mark(`${trackingId}-start`);
    return trackingId;
  }

  /**
   * End tracking and record metrics
   */
  endSearch(
    trackingId: string,
    searchType: string,
    resultsCount: number = 0,
  ): SearchPerformanceMetrics {
    const endMark = `${trackingId}-end`;
    performance.mark(endMark);

    try {
      performance.measure(trackingId, `${trackingId}-start`, endMark);
      const measure = performance.getEntriesByName(trackingId)[0];
      const duration = measure?.duration || 0;

      const metric: SearchPerformanceMetrics = {
        searchType,
        duration,
        timestamp: Date.now(),
        resultsCount,
        memoryUsage: this.getMemoryUsage(),
      };

      this.recordMetric(metric);
      this.cleanup();

      return metric;
    } catch (error) {
      // Fallback metric if performance API fails
      return {
        searchType,
        duration: 0,
        timestamp: Date.now(),
        resultsCount,
      };
    }
  }

  /**
   * Record search metric
   */
  private recordMetric(metric: SearchPerformanceMetrics): void {
    this.metrics.push(metric);

    // Keep only recent metrics
    if (this.metrics.length > this.MAX_METRICS) {
      this.metrics = this.metrics.slice(-this.MAX_METRICS);
    }

    // Persist to localStorage for analysis
    try {
      localStorage.setItem(this.STORAGE_KEY, JSON.stringify(this.metrics));
    } catch (error) {
      // Storage quota exceeded or disabled - continue without persistence
    }
  }

  /**
   * Get current memory usage
   */
  private getMemoryUsage(): SearchPerformanceMetrics['memoryUsage'] {
    if ('memory' in performance && (performance as any).memory) {
      const memory = (performance as any).memory;
      return {
        used: memory.usedJSHeapSize,
        total: memory.totalJSHeapSize,
        limit: memory.jsHeapSizeLimit,
      };
    }
    return undefined;
  }

  /**
   * Clean up old performance entries
   */
  private cleanup(): void {
    try {
      // Clear old performance entries to prevent memory leaks
      const entries = performance.getEntriesByType('measure');
      if (entries.length > 50) {
        performance.clearMeasures();
      }
    } catch (error) {
      // Cleanup failed - continue without clearing
    }
  }

  /**
   * Get performance statistics
   */
  getStats(): PerformanceStats {
    const totalSearches = this.metrics.length;

    if (totalSearches === 0) {
      return {
        averageLatency: 0,
        totalSearches: 0,
        searchTypes: {},
        memoryTrend: [],
      };
    }

    // Calculate average latency
    const totalDuration = this.metrics.reduce((sum, m) => sum + m.duration, 0);
    const averageLatency = totalDuration / totalSearches;

    // Count search types
    const searchTypes: Record<string, number> = {};
    this.metrics.forEach(m => {
      searchTypes[m.searchType] = (searchTypes[m.searchType] || 0) + 1;
    });

    // Memory trend (last 10 measurements with memory data)
    const memoryTrend = this.metrics
      .filter(m => m.memoryUsage)
      .slice(-10)
      .map(m => ({
        timestamp: m.timestamp,
        used: m.memoryUsage!.used,
      }));

    return {
      averageLatency,
      totalSearches,
      searchTypes,
      memoryTrend,
    };
  }

  /**
   * Get recent metrics
   */
  getRecentMetrics(count: number = 10): SearchPerformanceMetrics[] {
    return this.metrics.slice(-count);
  }

  /**
   * Clear all metrics
   */
  clearMetrics(): void {
    this.metrics = [];
    try {
      localStorage.removeItem(this.STORAGE_KEY);
      performance.clearMeasures();
      performance.clearMarks();
    } catch (error) {
      // Cleanup failed - continue
    }
  }

  /**
   * Load persisted metrics
   */
  loadPersistedMetrics(): void {
    try {
      const stored = localStorage.getItem(this.STORAGE_KEY);
      if (stored) {
        const parsed = JSON.parse(stored);
        if (Array.isArray(parsed)) {
          this.metrics = parsed.slice(-this.MAX_METRICS);
        }
      }
    } catch (error) {
      // Failed to load - start fresh
      this.metrics = [];
    }
  }

  /**
   * Check if performance is degrading
   */
  isPerformanceDegrading(): boolean {
    if (this.metrics.length < 10) return false;

    const recent = this.metrics.slice(-5);
    const previous = this.metrics.slice(-10, -5);

    const recentAvg =
      recent.reduce((sum, m) => sum + m.duration, 0) / recent.length;
    const previousAvg =
      previous.reduce((sum, m) => sum + m.duration, 0) / previous.length;

    // Performance is degrading if recent searches are 50% slower
    return recentAvg > previousAvg * 1.5;
  }
}

// Export singleton instance
export const searchPerformanceMonitor = new SearchPerformanceMonitor();

// Load persisted metrics on initialization
searchPerformanceMonitor.loadPersistedMetrics();

export type { PerformanceStats, SearchPerformanceMetrics };

/**
 * City Management Hook
 *
 * Manages favorite cities, recent locations, and city switching functionality.
 * Provides localStorage persistence and weather data coordination.
 */

import { useState, useEffect, useCallback } from 'react';
import { logError } from './logger';


export interface SavedCity {
  id: string;
  name: string;
  displayName: string;
  latitude: number;
  longitude: number;
  country?: string;
  state?: string;
  isFavorite: boolean;
  lastAccessed: number;
  addedAt: number;
}

export interface CityManagementState {
  favorites: SavedCity[];
  recentCities: SavedCity[];
  currentCity: SavedCity | null;
  isLoading: boolean;
  error: string | null;
}

const STORAGE_KEYS = {
  FAVORITES: 'weather-app-favorite-cities',
  RECENT: 'weather-app-recent-cities',
  CURRENT: 'weather-app-current-city',
} as const;

const MAX_RECENT_CITIES = 10;
const MAX_FAVORITE_CITIES = 20;

/**
 * useCityManagement - Custom React hook for useCityManagement functionality
 */
/**
 * useCityManagement - Custom React hook for useCityManagement functionality
 */
export const useCityManagement = () => {
  const [state, setState] = useState<CityManagementState>({
    favorites: [],
    recentCities: [],
    currentCity: null,
    isLoading: false,
    error: null,
  });

  // Load saved data from localStorage on mount
  const loadSavedData = useCallback(() => {
    try {
      const savedFavorites = localStorage.getItem(STORAGE_KEYS.FAVORITES);
      const savedRecent = localStorage.getItem(STORAGE_KEYS.RECENT);
      const savedCurrent = localStorage.getItem(STORAGE_KEYS.CURRENT);

      setState(prev => ({
        ...prev,
        favorites: savedFavorites ? JSON.parse(savedFavorites) : [],
        recentCities: savedRecent ? JSON.parse(savedRecent) : [],
        currentCity: savedCurrent ? JSON.parse(savedCurrent) : null,
        error: null,
      }));
    } catch (error) {
      logError('Failed to load saved city data:', error);
      setState(prev => ({
        ...prev,
        error: 'Failed to load saved cities',
      }));
    }
  }, []);

  useEffect(() => {
    loadSavedData();
  }, [loadSavedData]);

  // Generate unique ID for a city
  const generateCityId = useCallback(
    (name: string, latitude: number, longitude: number): string => {
      return `${name.toLowerCase().replace(/\s+/g, '-')}-${latitude.toFixed(4)}-${longitude.toFixed(4)}`;
    },
    [],
  );

  // Create SavedCity object from basic data
  const createCityObject = useCallback(
    (
      name: string,
      latitude: number,
      longitude: number,
      displayName?: string,
      country?: string,
      state?: string,
      isFavorite: boolean = false,
    ): SavedCity => {
      const now = Date.now();
      return {
        id: generateCityId(name, latitude, longitude),
        name,
        displayName: displayName || name,
        latitude,
        longitude,
        country,
        state,
        isFavorite,
        lastAccessed: now,
        addedAt: now,
      };
    },
    [generateCityId],
  );

  // Add city to favorites
  const addToFavorites = useCallback(
    (
      name: string,
      latitude: number,
      longitude: number,
      displayName?: string,
      country?: string,
      state?: string,
    ) => {
      setState(prev => {
        const cityId = generateCityId(name, latitude, longitude);

        // Check if already in favorites
        const existingIndex = prev.favorites.findIndex(
          city => city.id === cityId,
        );
        if (existingIndex !== -1) {
          // Update existing favorite
          const updatedFavorites = [...prev.favorites];
          updatedFavorites[existingIndex] = {
            ...updatedFavorites[existingIndex],
            lastAccessed: Date.now(),
            displayName: displayName || name,
            country,
            state,
          };
          return { ...prev, favorites: updatedFavorites };
        }

        // Add new favorite
        const newCity = createCityObject(
          name,
          latitude,
          longitude,
          displayName,
          country,
          state,
          true,
        );
        const updatedFavorites = [newCity, ...prev.favorites].slice(
          0,
          MAX_FAVORITE_CITIES,
        );

        return { ...prev, favorites: updatedFavorites };
      });
    },
    [generateCityId, createCityObject],
  );

  // Remove city from favorites
  const removeFromFavorites = useCallback((cityId: string) => {
    setState(prev => ({
      ...prev,
      favorites: prev.favorites.filter(city => city.id !== cityId),
    }));
  }, []);

  // Add city to recent history
  const addToRecent = useCallback(
    (
      name: string,
      latitude: number,
      longitude: number,
      displayName?: string,
      country?: string,
      state?: string,
    ) => {
      setState(prev => {
        const cityId = generateCityId(name, latitude, longitude);

        // Remove if already exists
        const filteredRecent = prev.recentCities.filter(
          city => city.id !== cityId,
        );

        // Add to front of list
        const newCity = createCityObject(
          name,
          latitude,
          longitude,
          displayName,
          country,
          state,
          false,
        );
        const updatedRecent = [newCity, ...filteredRecent].slice(
          0,
          MAX_RECENT_CITIES,
        );

        return { ...prev, recentCities: updatedRecent };
      });
    },
    [generateCityId, createCityObject],
  );

  // Set current city
  const setCurrentCity = useCallback(
    (
      name: string,
      latitude: number,
      longitude: number,
      displayName?: string,
      country?: string,
      state?: string,
    ) => {
      const city = createCityObject(
        name,
        latitude,
        longitude,
        displayName,
        country,
        state,
        false,
      );

      setState(prev => ({
        ...prev,
        currentCity: city,
      }));

      // Also add to recent history
      addToRecent(name, latitude, longitude, displayName, country, state);
    },
    [createCityObject, addToRecent],
  );

  // Check if city is in favorites
  const isFavorite = useCallback(
    (name: string, latitude: number, longitude: number): boolean => {
      const cityId = generateCityId(name, latitude, longitude);
      return state.favorites.some(city => city.id === cityId);
    },
    [state.favorites, generateCityId],
  );

  // Toggle favorite status
  const toggleFavorite = useCallback(
    (
      name: string,
      latitude: number,
      longitude: number,
      displayName?: string,
      country?: string,
      state?: string,
    ) => {
      if (isFavorite(name, latitude, longitude)) {
        const cityId = generateCityId(name, latitude, longitude);
        removeFromFavorites(cityId);
      } else {
        addToFavorites(name, latitude, longitude, displayName, country, state);
      }
    },
    [isFavorite, generateCityId, removeFromFavorites, addToFavorites],
  );

  // Clear all recent cities
  const clearRecentCities = useCallback(() => {
    setState(prev => ({ ...prev, recentCities: [] }));
    localStorage.removeItem(STORAGE_KEYS.RECENT);
  }, []);

  // Clear all favorites
  const clearFavorites = useCallback(() => {
    setState(prev => ({ ...prev, favorites: [] }));
    localStorage.removeItem(STORAGE_KEYS.FAVORITES);
  }, []);

  // Get combined city list for quick access (favorites + recent)
  const getQuickAccessCities = useCallback((): SavedCity[] => {
    const allCities = [...state.favorites, ...state.recentCities];
    const uniqueCities = allCities.filter(
      (city, index, self) => index === self.findIndex(c => c.id === city.id),
    );

    // Sort by: favorites first, then by last accessed
    return uniqueCities.sort((a, b) => {
      if (a.isFavorite && !b.isFavorite) return -1;
      if (!a.isFavorite && b.isFavorite) return 1;
      return b.lastAccessed - a.lastAccessed;
    });
  }, [state.favorites, state.recentCities]);

  // Export city data
  const exportCityData = useCallback(() => {
    return {
      favorites: state.favorites,
      recentCities: state.recentCities,
      exportedAt: new Date().toISOString(),
      version: '1.0',
    };
  }, [state.favorites, state.recentCities]);

  // Import city data
  const importCityData = useCallback(
    (data: {
      favorites?: SavedCity[];
      recentCities?: SavedCity[];
      version?: string;
    }) => {
      try {
        if (data.favorites && Array.isArray(data.favorites)) {
          setState(prev => ({
            ...prev,
            favorites: data.favorites!.slice(0, MAX_FAVORITE_CITIES),
            recentCities: data.recentCities
              ? data.recentCities.slice(0, MAX_RECENT_CITIES)
              : prev.recentCities,
            error: null,
          }));
          return true;
        }
        throw new Error('Invalid city data format');
      } catch (err) {
        logError('Failed to import city data:', err);
        setState(prev => ({
          ...prev,
          error: 'Failed to import city data',
        }));
        return false;
      }
    },
    [],
  );

  return {
    // State
    favorites: state.favorites,
    recentCities: state.recentCities,
    currentCity: state.currentCity,
    isLoading: state.isLoading,
    error: state.error,

    // Actions
    addToFavorites,
    removeFromFavorites,
    addToRecent,
    setCurrentCity,
    toggleFavorite,
    clearRecentCities,
    clearFavorites,

    // Utilities
    isFavorite,
    getQuickAccessCities,
    exportCityData,
    importCityData,
    loadSavedData,

    // Constants
    maxFavorites: MAX_FAVORITE_CITIES,
    maxRecent: MAX_RECENT_CITIES,
  };
};
